pragma solidity 0.6.12;

contract MultisigWallet {
    mapping (address => uint256) public isowner;
    mapping (uint256 => mapping (address => uint256)) public confirmations;
    mapping (uint256 => mapping (uint256 => mapping (uint256 => uint256))) private allowance1;
    address[] public owners;
    uint256 public required;
    uint256 public transactionids;
    uint256 public calcmaxwithdraw;
    uint256 public lastday;
    uint256 public spenttoday;

    function revokeConfirmation(uint256 transactionId) public {
        require(isowner[msg.sender] > 0, "Not owner");
        require(confirmations[transactionId][msg.sender] > 0, "No confirmation from owner");
        require(allowance1[transactionId][3][0] == 0, "Transaction executed");
        confirmations[transactionId][msg.sender] = 0;
    }

    function removeOwner(address owner) public {
        require(isowner[owner] > 0, "Not owner");
        isowner[owner] = 0;
        for (uint i = 0; i < owners.length - 1; i++)
            if (owners[i] == owner) {
                owners[i] = owners[owners.length - 1];
                break;
            }
        owners.pop();
        if (owners.length < required)
            required = owners.length;
        for (uint i = 0; i < owners.length; i++)
            confirmations[transactionids][owners[i]] = 0;
    }

    function changeDailyLimit(uint256 _newLimit) public {
        calcmaxwithdraw = _newLimit;
    }

    function changeRequirement(uint256 _newRequired) public {
        require(_newRequired > 0 && _newRequired <= owners.length, "Invalid requirement");
        required = _newRequired;
    }

    function addOwner(address owner) public {
        require(isowner[owner] == 0, "Already owner");
        isowner[owner] = 1;
        owners.push(owner);
    }

    function replaceOwner(address oldOwner, address newOwner) public {
        require(isowner[oldOwner] > 0, "Not owner");
        require(isowner[newOwner] == 0, "New owner already exists");
        for (uint i = 0; i < owners.length; i++)
            if (owners[i] == oldOwner) {
                owners[i] = newOwner;
                break;
            }
        isowner[oldOwner] = 0;
        isowner[newOwner] = 1;
    }

    function confirmTransaction(uint256 transactionId) public {
        require(isowner[msg.sender] > 0, "Not owner");
        confirmations[transactionId][msg.sender] = 1;
    }

    function executeTransaction(uint256 transactionId) public payable {
        require(allowance1[transactionId][3][0] == 0, "Transaction already executed");
        require(allowance1[transactionId][2][0] <= spenttoday, "Daily limit reached");
        allowance1[transactionId][3][0] = 1;
        spenttoday += allowance1[transactionId][1][0];
        msg.sender.transfer(allowance1[transactionId][1][0]);
    }
}
```

