pragma solidity 0.6.12;

contract Contract {
    address public owner;
    mapping (address => uint256) public tokenbalance;
    mapping (address => uint256) public refbalance;
    uint256 public tokenprice;
    uint256 public tokensupply;
    uint256 public start;
    uint256 public softcapmoment;
    uint256 public softcapperiod;
    uint256 public hardcapperiod;
    uint256 public investedtotal;
    bool public softcapreached;

    constructor() public payable {
        require(msg.value == 0, "Constructor function is not payable");
        tokenprice = 0x16bcc41e90000;
        tokensupply = 0;
        softcapmoment = 0;
        softcapperiod = 0x16a224;
        hardcapperiod = 0x16a224;
        investedtotal = 0;
        softcapreached = false;
        owner = msg.sender;
        start = block.timestamp;
    }

    function transfer(address _to) public {
        require(tokenbalance[msg.sender] > 0, "Not enough balance");
        tokenbalance[_to] += tokenbalance[msg.sender];
        tokenbalance[msg.sender] = 0;
    }

    function withdrawAmount(uint256 _amount) public {
        require(softcapreached == true && _amount > 0 && refbalance[msg.sender] >= _amount, "Not enough balance or softcap not reached");
        refbalance[msg.sender] -= _amount;
        msg.sender.call{value: _amount}("");
    }

    function deinvest() public {
        require(softcapreached == false && tokenbalance[msg.sender] > 0 && block.timestamp > start + softcapperiod, "Not enough balance or softcap reached or not in softcap period");
        tokensupply -= tokenbalance[msg.sender];
        msg.sender.call{value: (tokenbalance[msg.sender] * tokenprice) / 0xde0b6b3a7640000}("");
        tokenbalance[msg.sender] = 0;
    }

    function withdraw() public {
        require(softcapreached == true && refbalance[msg.sender] > 0, "Not enough balance or softcap not reached");
        msg.sender.call{value: refbalance[msg.sender]}("");
        refbalance[msg.sender] = 0;
    }

    fallback() external payable {
        require(((0xde0b6b3a7640000 * (msg.value / tokenprice)) + tokensupply) <= 0x84595161401484a000000, "Supply limit exceeded");
        tokenbalance[msg.sender] += (0xde0b6b3a7640000 * (msg.value / tokenprice));
        tokensupply += (0xde0b6b3a7640000 * (msg.value / tokenprice));
        refbalance[owner] += ((msg.value * 6) / 100);
        refbalance[owner] += ((msg.value * 3) / 100);
        refbalance[owner] += (msg.value - ((msg.value * 6) / 100) - ((msg.value * 3) / 100));
        investedtotal += msg.value;
        if (0x211654585005212800000 <= ((0xde0b6b3a7640000 * (msg.value / tokenprice)) + tokensupply)) {
            softcapreached = true;
            softcapmoment = block.timestamp;
        }
    }

    function withdrawAmountTo(uint256 _amount, address _to) public {
        require(softcapreached == true && _amount > 0 && refbalance[msg.sender] >= _amount, "Not enough balance or softcap not reached");
        refbalance[msg.sender] -= _amount;
        _to.call{value: _amount}("");
    }

    function goESM() public {
        require(softcapreached == true && tokenbalance[msg.sender] > 0, "Not enough balance or softcap not reached");
        tokensupply -= tokenbalance[msg.sender];
        tokenbalance[msg.sender] = 0;
    }

    function buy(address _referrer, address _referrer2) public payable {
        require(msg.value > 0 && ((0xde0b6b3a7640000 * (msg.value / tokenprice)) + tokensupply) <= 0x84595161401484a000000, "Not enough Ether or supply limit exceeded");
        tokenbalance[msg.sender] += (0xde0b6b3a7640000 * (msg.value / tokenprice));
        tokensupply += (0xde0b6b3a7640000 * (msg.value / tokenprice));
        investedtotal += msg.value;
        if (_referrer == address(0)) {
            refbalance[owner] += ((msg.value * 6) / 100);
        } else {
            if (tokenbalance[_referrer] >= 0x6c6b935b8bbd40000) {
                refbalance[_referrer] += ((msg.value * 6) / 100);
            } else {
                refbalance[owner] += ((msg.value * 6) / 100);
            }
        }
        if (_referrer2 == address(0)) {
            refbalance[owner] += ((msg.value * 3) / 100);
        } else {
            if (tokenbalance[_referrer2] >= 0x6c6b935b8bbd40000) {
                refbalance[_referrer2] += ((msg.value * 3) / 100);
            } else {
                refbalance[owner] += ((msg.value * 3) / 100);
            }
        }
        refbalance[owner] += (msg.value - ((msg.value * 6) / 100) - ((msg.value * 3) / 100));
        if (0x211654585005212800000 <= ((0xde0b6b3a7640000 * (msg.value / tokenprice)) + tokensupply)) {
            softcapreached = true;
            softcapmoment = block.timestamp;
        }
    }
}
