Here's the solidity code based on the given decompiled code:

```solidity
pragma solidity ^0.5.0;

contract MyContract {
    uint256 private _deadline;
    uint256 private _amountRaised;
    uint256 private _price;
    mapping (address => uint256) private _balanceOf;
    address payable private _payoutAddr;
    bool private _fallback;
    IToken private _tokenReward;

    event FundTransfer(address indexed _backer, uint256 _amount, bool _isContribution);

    function deadline() public view returns (uint256) {
        return _deadline;
    }

    function payoutAddr() public view returns (address) {
        return _payoutAddr;
    }

    function tokenReward() public view returns (address) {
        return address(_tokenReward);
    }

    function balanceOf(address _address) public view returns (uint256) {
        return _balanceOf[_address];
    }

    function amountRaised() public view returns (uint256) {
        return _amountRaised;
    }

    function price() public view returns (uint256) {
        return _price;
    }

    function closeSale() public {
        require(msg.sender == _payoutAddr, "Only payout address can close the sale");
        if (block.timestamp >= _deadline) {
            _fallback = true;
        }
    }

    function safeWithdrawal() public {
        require(block.timestamp >= _deadline && msg.sender == _payoutAddr, "Only payout address can withdraw after deadline");

        (bool success, ) = _payoutAddr.call.value(_amountRaised)("");
        require(success, "Transfer failed.");

        if (success) {
            emit FundTransfer(_payoutAddr, _amountRaised, false);
            _amountRaised = 0;
        }
    }

    function fallback() public payable {
        require(!_fallback, "Sale is closed");
        _balanceOf[msg.sender] += msg.value;
        _amountRaised += msg.value;
        require(_tokenReward.transfer(msg.sender, msg.value * _price), "Transfer failed.");
        emit FundTransfer(msg.sender, msg.value, true);
    }
}

interface IToken {
    function transfer(address receiver, uint amount) external returns (bool);
}
```
Please note that this contract requires the IToken interface to be implemented for `_tokenReward`. The IToken contract should have a `transfer` function that sends tokens from this contract to another address. If such a contract doesn't exist, you'll need to create it.

