pragma solidity 0.6.12;

contract YourContract {
    string public name;
    uint256 public owner;
    address public poolsize;
    uint256 public daiaddress;
    address public daioracleaddress;

    function updateTokenAddress(address _address, int128 _int) public {
        require(msg.sender == owner);
        require(_int < 5);
        require(_int <= int128(uint128(0x7fffffffffffffffffffffffffffffff)));
        require(_int >= int128(uint128(0xffffffffffffffffffffffffffffffff80000000000000000000000000000000)));

        if (_int == 0) {
            poolsize = _address;
        }
    }

    function normalized_token_prices(address _address) public {
        require(_address != address(this));

        (bool success,) = _address.delegatecall(abi.encodePacked(bytes4(0x313ce567)));
        require(success, "delegatecall failed");

        if (_address != daiaddress && daioracleaddress != address(this)) {
            (success,) = daioracleaddress.delegatecall(abi.encodePacked(bytes4(0xafb1f8d6)));
            require(success, "delegatecall failed");
        }
    }

    function poolSize(address _address) public {
        require(poolsize != 0 && poolsize != address(this));

        (bool success,) = poolsize.delegatecall(abi.encodePacked(bytes4(0x70a08231), _address));
        require(success, "delegatecall failed");

        this.normalized_token_prices(poolsize);
    }
}
```
Please ensure to replace `"YourContract"` with your desired contract name. This contract allows an owner to update the token address, calculate normalized token prices, and manage the pool size. Each function includes the necessary requirements and conditions mentioned in the description. The contract utilizes delegate calls to make external function calls. Please note that this contract does not include a constructor or a method to set the `owner`, `poolsize`, `daiaddress`, and `daioracleaddress` variables.
