Sure, here is a simple version of the contract code in Solidity:

```solidity
pragma solidity ^0.6.12;

contract Token {
    string public name;
    string public symbol;
    uint256 public decimals;
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public totalSupply;
    uint256 private totalSupply2;
    uint256 public contractFrozen;
    mapping (address => uint256) public blacklisted;
    mapping (address => bool) public isIssuer;

    function setIssuer(address _issuer, bool _value) public {
        require(totalSupply2 < 2, "Total supply too high");
        require(isIssuer[_issuer] != _value, "Issuer status is the same");
        isIssuer[_issuer] = _value;
    }

    function freezeContract(bool _value) public {
        require(totalSupply2 < 2, "Total supply too high");
        contractFrozen = _value ? 1 : 0;
    }

    function blacklist(address _user) public {
        require(totalSupply2 >= 0, "Total supply too low");
        require(_user != address(0), "Invalid address");
        blacklisted[_user] = 1;
    }

    function transfer(address _to, uint256 _value) public {
        require(blacklisted[msg.sender] != 1, "Sender is blacklisted");
        require(contractFrozen != 1, "Contract is frozen");
        require(balanceOf[msg.sender] >= _value, "Insufficient balance");
        balanceOf[msg.sender] -= _value;
        require(balanceOf[_to] + _value >= balanceOf[_to], "Overflow error");
        balanceOf[_to] += _value;
    }

    function approve(address _spender, uint256 _value) public {
        require(blacklisted[msg.sender] != 1, "Sender is blacklisted");
        require(contractFrozen != 1, "Contract is frozen");
        allowance[msg.sender][_spender] = _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(balanceOf[_from] >= _value, "Insufficient balance");
        require(blacklisted[msg.sender] != 1, "Sender is blacklisted");
        require(contractFrozen != 1, "Contract is frozen");
        balanceOf[_from] -= _value;
        require(balanceOf[_to] + _value >= balanceOf[_to], "Overflow error");
        balanceOf[_to] += _value;
        require(allowance[_from][msg.sender] >= _value, "Insufficient allowance");
        allowance[_from][msg.sender] -= _value;
    }

    function mint(address _to, uint256 _value) public {
        require(isIssuer[msg.sender] == true, "Only issuer can mint");
        require(_to != address(0), "Invalid address");
        require(totalSupply + _value >= totalSupply, "Overflow error");
        totalSupply += _value;
        require(balanceOf[_to] + _value >= balanceOf[_to], "Overflow error");
        balanceOf[_to] += _value;
    }

    function redeem(uint256 _value) public {
        require(isIssuer[msg.sender] == true, "Only issuer can redeem");
        require(totalSupply >= _value, "Insufficient total supply");
        totalSupply -= _value;
        require(balanceOf[msg.sender] >= _value, "Insufficient balance");
        balanceOf[msg.sender] -= _value;
    }

    function burnFrom(address _from, uint256 _value) public {
        require(isIssuer[msg.sender] == true, "Only issuer can burn");
        require(_from != address(0), "Invalid address");
        require(totalSupply >= _value, "Insufficient total supply");
        totalSupply -= _value;
        require(balanceOf[_from] >= _value, "Insufficient balance");
        balanceOf[_from] -= _value;
    }
}
```

This contract code is quite straightforward. It basically implements a simple token contract with additional features like issuing, freezing, blacklisting, minting, redeeming and burning.
