pragma solidity 0.6.12;

contract MyContract {
    uint256 public totalsupply;
    address public owner;
    address public transfer_proxy;
    mapping(address => uint256) private balanceOf2;
    string public name;
    string public symbol;
    uint256 public decimals;
    address public originaltoken;
    mapping(address => uint256) public depositlock;
    mapping(address => uint256) public balanceof;

    constructor() public payable {
        originaltoken = address(0);
    }

    function isValidSignature(bytes32 _hash, uint8 _v, bytes32 _r, bytes32 _s) public pure returns (bool) {
        return ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", _hash)), _v, _r, _s) != address(0);
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        balanceof[_to] += _value;
        balanceof[_from] -= _value;
    }

    function addSigner(address _signer) public {
        require(balanceOf2[msg.sender] != 0);
        balanceOf2[_signer] = 1;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0));
        owner = newOwner;
    }

    function deposit(uint256 _value, uint256 _locktime) public payable {
        require(_locktime >= 1 && ((_locktime * 3600) + block.timestamp) >= depositlock[msg.sender]);
        balanceof[msg.sender] += msg.value;
        depositlock[msg.sender] = (_locktime * 3600) + block.timestamp;
    }

    function withdraw(uint8 _v, bytes32 _r, bytes32 _s, uint256 _value, uint256 _block) public {
        require(balanceof[msg.sender] >= _value);
        require(block.number > depositlock[msg.sender]);
        balanceof[msg.sender] -= _value;
        msg.sender.transfer(_value);
        if(_block > block.number && block.number < depositlock[msg.sender]) {
            bytes32 hash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", sha256(abi.encodePacked(msg.sender, this, _block, _value, _v, _r, _s))));
            address signer = ecrecover(hash, _v, _r, _s);
            require(balanceOf2[signer] != 0);
            balanceof[msg.sender] -= _value;
            msg.sender.transfer(_value);
        }
    }

    function withdrawDifferentToken(address _token, bool _all) public {
        require(msg.sender == owner);
        (bool success, bytes memory data) = _token.call(abi.encodeWithSignature("balanceOf(address)", address(this)));
        require(success);
        uint256 balance = abi.decode(data, (uint256));
        require(balance > 0);
        bytes memory data2;
        if(_all) {
            data2 = abi.encodeWithSignature("transfer(address,uint256)", msg.sender, balance);
        } else {
            (success, data) = _token.call(abi.encodeWithSignature("balanceOf(address)", address(this)));
            require(success);
            uint256 half = abi.decode(data, (uint256)) / 2;
            data2 = abi.encodeWithSignature("transfer(address,uint256)", msg.sender, half);
        }
        (success,) = _token.call(data2);
        require(success);
    }
}
