Here is a Solidity contract based on your description:

```solidity
pragma solidity 0.6.12;

contract ICOContract {
    uint128 private addressOfBountyMembers = 0xde0b6b3a7640000;
    address payable public beneficiary = 0x8f42914C201AcDd8a2769211C862222Ec56eea40;
    address public owner = beneficiary;
    uint256 public startdate = block.timestamp;
    uint256 public deadlinepreicoone = 0x5a501144;
    uint256 public deadlinepreicotwo = 0x5a594bc4;
    uint256 public deadline = 0x5a837bc4;
    uint256 public vminetherperpurchase = 0x3e871b540c000;
    uint256 public vmaxetherperpurchase = 0xc328093e61ee40000;
    uint256 public price = 0x15ef994060200;
    uint256 public updatedprice = 0x1d48edaeb2a00;
    uint256 public amountraised;
    uint256 public senttoken;
    address public tokenreward = 0xDD5A3aEEf473401c23f24C4c6B9cD1B0808fbB36;
    mapping(address => uint256) public balanceof;
    bool private paused1 = false;
    bool private paused = false;

    constructor() public payable {
        require(msg.value > 0, "No ether provided");
    }

    modifier onlyOwner {
        require(msg.sender == owner, "Access denied");
        _;
    }

    function UpdatePrice(uint256 _price) public onlyOwner {
        updatedprice = _price;
        paused1 = true;
    }

    function kill() public onlyOwner {
        selfdestruct(beneficiary);
    }

    function EmergencyPause() public onlyOwner {
        paused = true;
    }

    function safeWithdrawal(uint256 amount) public onlyOwner {
        require(beneficiary.send(amount));
    }

    fallback() external payable {
        require(!paused, "Paused");
        if(!paused1) {
            if(block.timestamp < deadlinepreicoone) {
                price = 0x15ef994060200;
            } else if(block.timestamp < deadlinepreicotwo) {
                price = 0x1765f9cb83800;
            } else if(block.timestamp < deadline) {
                price = 0x1d48edaeb2a00;
            } else {
                price = 0x1d48edaeb2a00;
            }
        } else {
            if(paused1) {
                price = updatedprice * 0xde0b6b3a7640000;
            } else {
                price = 0x1d48edaeb2a00;
            }
        }
        require(msg.value >= vminetherperpurchase && msg.value <= vmaxetherperpurchase, "Invalid amount");
        balanceof[msg.sender] += msg.value;
        (bool success,) = tokenreward.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, 0));
        require(success, "Transfer failed");
        (success,) = tokenreward.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, (msg.value * addressOfBountyMembers) / price));
        require(success, "Transfer failed");
        if(msg.value == 0) {
            senttoken += 0;
        } else {
            senttoken += (msg.value * addressOfBountyMembers) / price;
        }
    }

    function EmergencyUnPause() public onlyOwner {
        paused = false;
    }
}
```
Please note that the contract is a rough implementation according to the description you provided. It's highly recommended to use SafeMath library for arithmetic operations to prevent from potential overflows/underflows. And please ensure to conduct thorough testing as well as security audit before deploying it to the mainnet.
