Here is the contract code based on your requirements:

```solidity
pragma solidity ^0.6.12;

contract YourContract {
    // State variables
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    address payable public multisigwallet;
    bool public active;
    address public owner;
    uint256 public onetokeninwei;

    // Payable constructor
    constructor(address payable _multisigwallet) public payable {
        owner = msg.sender;
        active = true;
        onetokeninwei = 0x8bec19599db8;
        multisigwallet = _multisigwallet;
    }

    // Functions
    function approve(address _to, uint256 _value) public {
        allowance[msg.sender][_to] = _value;
    }

    function withdraw() public {
        require(msg.sender == owner, "Only owner can withdraw");
        multisigwallet.transfer(address(this).balance);
    }

    function finalize() public {
        require(msg.sender == owner, "Only owner can finalize");
        active = false;
    }

    function decreaseApproval(address _to, uint256 _value) public {
        if(_value > allowance[msg.sender][_to]){
            allowance[msg.sender][_to] = 0;
        }
        else {
            allowance[msg.sender][_to] -= _value;
        }
    }

    fallback() external payable {
        require(active && msg.value > 0 && onetokeninwei != 0 && 0x1743b34e18439b502000000 >= totalsupply + (msg.value * 0xde0b6b3a7640000) / onetokeninwei);
        balanceof[msg.sender] += (msg.value * 0xde0b6b3a7640000) / onetokeninwei;
        totalsupply += (msg.value * 0xde0b6b3a7640000) / onetokeninwei;
    }

    function burn(uint256 _value) public {
        require(_value > 0);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(0));
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function setTokenPriceInWei(uint256 _price) public {
        require(msg.sender == owner, "Only owner can set price");
        onetokeninwei = _price;
    }

    function increaseApproval(address _to, uint256 _value) public {
        allowance[msg.sender][_to] += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0));
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function mintTokens(address _to, uint256 _value) public {
        require(msg.sender == owner, "Only owner can mint tokens");
        require(0x1743b34e18439b502000000 >= totalsupply + (_value * 0xde0b6b3a7640000));
        balanceof[_to] += (_value * 0xde0b6b3a7640000);
        totalsupply += (_value * 0xde0b6b3a7640000);
    }
}
```
Please note that I've provided a very basic version of the contract to match your requirements. For production use, it's highly recommended to have more checks and security measures in place, including checks against overflow/underflow operations, checking if the address is valid before transfers, and more. Also, you might want to use events to log important actions in your contract.
