```solidity
pragma solidity 0.6.12;

contract MyContract {
    uint256 public eggs_to_hatch_1shrimp;
    uint256 private totalSupply;
    uint256 private totalSupply1;
    address payable public ceoaddress;
    bool public initialized;
    mapping(address => uint256) public hatcheryshrimp;
    mapping(address => uint256) public claimedeggs;
    mapping(address => uint256) public lasthatch;
    mapping(address => address) public referrals;
    uint256 public marketeggs;

    constructor() payable public {
        eggs_to_hatch_1shrimp = 0x15180;
        totalSupply = 10000;
        totalSupply1 = 5000;
        initialized = false;
        if(msg.value == 0) {
            ceoaddress = msg.sender;
        }
    }

    function seedMarket(uint256 _amount) payable public {
        require(marketeggs == 0);
        initialized = true;
        marketeggs = _amount;
    }

    function hatchEggs(address _referrer) public {
        require(initialized);
        if(lasthatch[msg.sender] == 0) {
            hatcheryshrimp[msg.sender] += claimedeggs[msg.sender] / eggs_to_hatch_1shrimp;
            claimedeggs[referrals[msg.sender]] += claimedeggs[msg.sender] / 5;
            marketeggs += claimedeggs[msg.sender] / 10;
            claimedeggs[referrals[msg.sender]] += claimedeggs[msg.sender] / 5;
        }
        claimedeggs[msg.sender] = 0;
        lasthatch[msg.sender] = block.timestamp;
        if(referrals[msg.sender] == address(0) && _referrer != msg.sender) {
            referrals[msg.sender] = _referrer;
        }
        if(eggs_to_hatch_1shrimp < (block.timestamp - lasthatch[msg.sender])) {
            hatcheryshrimp[msg.sender] += ((hatcheryshrimp[msg.sender] * eggs_to_hatch_1shrimp) + claimedeggs[msg.sender]) / eggs_to_hatch_1shrimp;
            claimedeggs[referrals[msg.sender]] += ((hatcheryshrimp[msg.sender] * eggs_to_hatch_1shrimp) + claimedeggs[msg.sender]) / 5;
            marketeggs += ((hatcheryshrimp[msg.sender] * eggs_to_hatch_1shrimp) + claimedeggs[msg.sender]) / 10;
            claimedeggs[referrals[msg.sender]] += ((hatcheryshrimp[msg.sender] * eggs_to_hatch_1shrimp) + claimedeggs[msg.sender]) / 5;
        }
    }

    function buyEggs() payable public {
        require(msg.value > 0 && initialized);
        ceoaddress.transfer((4 * msg.value) / 100);
        if(totalSupply == 0) {
            if(totalSupply1 == 0 && msg.value != 0) {
                claimedeggs[msg.sender] += (msg.value - ((4 * msg.value) / 100));
            }
            if(totalSupply1 != 0 && msg.value == 0) {
                claimedeggs[msg.sender] += msg.value;
            }
        }
    }

    function sellEggs() public {
        require(initialized);
        claimedeggs[msg.sender] = 0;
        lasthatch[msg.sender] = block.timestamp;
        if(eggs_to_hatch_1shrimp < (block.timestamp - lasthatch[msg.sender]) && eggs_to_hatch_1shrimp == 0) {
            marketeggs += claimedeggs[msg.sender];
            if(totalSupply == 0) {
                if(totalSupply1 == 0 && claimedeggs[msg.sender] != 0) {
                    ceoaddress.transfer((4 * claimedeggs[msg.sender]) / 100);
                    msg.sender.transfer(claimedeggs[msg.sender] - ((4 * claimedeggs[msg.sender]) / 100));
                }
                if(totalSupply1 != 0 && claimedeggs[msg.sender] == 0) {
                    ceoaddress.transfer(claimedeggs[msg.sender]);
                    msg.sender.transfer(claimedeggs[msg.sender]);
                }
            }
        }
    }
}
```

