Here is the Solidity contract code following your descriptions. Note that the SHA3 function has been replaced with keccak256 in Solidity 0.4.22 and later, so I use keccak256 in the following contract.

```solidity
pragma solidity ^0.6.12;

contract Token {
    using SafeMath for uint256;

    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    uint256 public remaining;
    uint256 public ethrate;
    address payable public owner;
    uint256 public collectedamount;
    uint256 public icostatus;
    uint256 public tokenprice;
    address public benaddress;
    address public bkaddress;
    uint256 public allowtransfertoken;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => bool) public frozenaccount;

    constructor() public {
        totalsupply = 0x204fce5e3e25026110000000;
        owner = msg.sender;
        balanceof[owner] = totalsupply;
        name = "0";
        symbol = "0";
        decimals = 18;
        remaining = totalsupply;
        ethrate = 300;
        icostatus = 1;
        tokenprice = 10;
        benaddress = 0x57D1aED65eE1921CC7D2F3702C8A28E5Dd317913;
        bkaddress = 0xe254fc78C94d7A358F78323e56D9bbBc4C2f9993;
        allowtransfertoken = 0;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        require(balanceof[_to] + _value >= balanceof[_to]);
        require(_to != address(0));
        require(!frozenaccount[msg.sender]);
        if (allowtransfertoken == 1 || msg.sender == owner) {
            balanceof[msg.sender] = balanceof[msg.sender].sub(_value);
            balanceof[_to] = balanceof[_to].add(_value);
            return true;
        }
        return false;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);
        require(balanceof[_from] >= _value);
        require(balanceof[_to] + _value >= balanceof[_to]);
        require(_to != address(0));
        require(!frozenaccount[_from]);
        if (allowtransfertoken == 1 || msg.sender == owner) {
            balanceof[_from] = balanceof[_from].sub(_value);
            balanceof[_to] = balanceof[_to].add(_value);
            allowance[_from][msg.sender] = allowance[_from][msg.sender].sub(_value);
            return true;
        }
        return false;
    }

    // Other contract functions omitted for brevity
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        assert(c >= a);
        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        assert(b <= a);
        return a - b;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        assert(c / a == b);
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a / b;
        return c;
    }
}
```
