Here is the Solidity contract based on your requirements:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    address public owner;
    mapping(address => uint256) public deposited;
    uint8 public state;
    address payable public wallet;

    constructor(address payable _wallet) public {
        owner = msg.sender;
        wallet = _wallet;
        state = 0;
    }

    modifier onlyOwner {
        require(msg.sender == owner, "You are not the owner");
        _;
    }

    modifier stateCheck {
        require(state <= 2, "State is greater than 2");
        _;
    }

    function close() external onlyOwner stateCheck {
        require(state == 0, "State is not 0");
        state = 2;
        (bool success, ) = wallet.call{value: address(this).balance}("");
        require(success, "Transfer failed.");
    }

    function enableRefunds() external onlyOwner stateCheck {
        require(state == 0, "State is not 0");
        state = 1;
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "New owner is the zero address");
        owner = newOwner;
    }

    function refund(address payable refundAddress) external {
        require(state == 1, "State is not 1");
        require(state <= 2, "State is greater than 2");
        uint256 amount = deposited[refundAddress];
        require(amount > 0, "No funds to refund");
        (bool success, ) = refundAddress.call{value: amount}("");
        require(success, "Refund failed.");
        deposited[refundAddress] = 0;
    }

    function deposit(address depositor) external payable onlyOwner stateCheck {
        require(state == 0, "State is not 0");
        uint256 amount = msg.value + deposited[depositor];
        require(amount >= deposited[depositor], "Deposit failed due to overflow");
        deposited[depositor] = amount;
    }
}
```

This contract compiles with Solidity 0.6.12 and uses the newer `.call{value: ...}("")` syntax for transferring ether. It also includes checks for state conditions and owner-only function access. Overflow is checked in the deposit function. The contract has a constructor which sets the initial owner and wallet address.
