Sure, the following is a Solidity contract that meets your requirements:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    address payable public owner;
    mapping (address => uint256) public balanceof;
    mapping (address => uint256) public freezeof;
    mapping (address => mapping (address => uint256)) public allowance;

    constructor() public {
      owner = msg.sender;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function withdrawEther(uint256 amount) public {
        require(msg.sender == owner);
        owner.transfer(amount);
    }

    function burn(uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value && _value > 0);
        balanceof[msg.sender] -= _value;
        if (totalsupply >= _value) {
            totalsupply -= _value;
        }
        return true;
    }

    function unfreeze(uint256 _value) public payable returns (bool success) {
        require(freezeof[msg.sender] >= _value && _value > 0);
        freezeof[msg.sender] -= _value;
        balanceof[msg.sender] += _value;
        return true;
    }

    function freeze(uint256 _value) public payable returns (bool success) {
        require(balanceof[msg.sender] >= _value && _value > 0);
        balanceof[msg.sender] -= _value;
        freezeof[msg.sender] += _value;
        return true;
    }

    function transfer(address _to, uint256 _value) public payable returns (bool success) {
        require(balanceof[msg.sender] >= _value && _value > 0);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public payable returns (bool success) {
        require(_to != address(0) && _value <= balanceof[_from] && _value <= allowance[_from][msg.sender] && _value > 0);
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        return true;
    }

}
```

Please note that this is a simple contract and does not include any form of error handling or security measures. It's highly recommended to add those in a production environment.
