Here is the contract code according to your requirements:

```solidity
pragma solidity ^0.6.12;

contract YourContract {
    address payable private owner;
    uint256 public targetamount;
    uint256 public finalblock;
    uint256 private totalSupply;
    address payable public miner;
    uint256 public target;

    constructor() payable public {
        owner = 0x935F545C5aA388B6846FB7A4c51ED1b180A4eFFF;
        targetamount = 1;
        finalblock = 0x5f5e100;
        totalSupply = 0x6acfc0;
        miner = 0x0;
        target = 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;
    }

    function payout() external {
        require(block.number >= finalblock, "Final block not reached");
        uint256 balance = address(this).balance - msg.value;
        if (block.number >= finalblock + 11000) {
            (bool success, ) = owner.call{value: balance / 2}("");
            require(success, "Transfer failed.");
            targetamount = targetamount / 2;
            target = 0x7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;
            totalSupply = block.number - 11000;
            finalblock = block.number + 11000;
        }
        (bool success, ) = miner.call{value: balance / 2 + balance / 4}("");
        require(success, "Transfer failed.");
        (success, ) = owner.call{value: balance / 8}("");
        require(success, "Transfer failed.");
        targetamount = balance / 64;
        target = 0x7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;
        totalSupply = block.number - 11000;
        finalblock = block.number + 11000;
    }

    fallback() external payable {
        uint256 balance = address(this).balance - msg.value;
        bytes32 sha3Value = keccak256(abi.encodePacked(target, balance, msg.value));
        if (uint256(sha3Value)/1 - msg.value < target && msg.value >= targetamount) {
            target = uint256(sha3Value)/1 - msg.value;
            miner = msg.sender;
            targetamount = msg.value;
            finalblock = block.number + block.number - totalSupply + 42;
            totalSupply = block.number;
            if (finalblock > block.number + 11000) {
                finalblock = block.number + 11000;
            }
        }
        if (block.number >= finalblock) {
            if (block.number >= finalblock + 11000) {
                (bool success, ) = owner.call{value: balance / 2}("");
                require(success, "Transfer failed.");
                targetamount = targetamount / 2;
                target = 0x7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;
                totalSupply = block.number - 11000;
                finalblock = block.number + 11000;
            }
            (bool success, ) = miner.call{value: balance / 2 + balance / 4}("");
            require(success, "Transfer failed.");
            (success, ) = owner.call{value: balance / 8}("");
            require(success, "Transfer failed.");
            targetamount = balance / 64;
            target = 0x7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;
            totalSupply = block.number - 11000;
            finalblock = block.number + 11000;
        }
    }
}
```

Please, keep in mind that the fallback function has a lot of computation and might exceed the block's gas limit. Therefore, it's better to refactor your code to make it more efficient and less gas consuming. Also, this contract would be more secure with SafeMath operations, but I followed your instructions and didn't include any external libraries. Always remember to test your contract on the testnet before deploying it to the mainnet.
