pragma solidity ^0.6.12;

contract MyContract {
    bool public paused;
    address public owner;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    uint256 public currentsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => bool) public frozenaccount;

    constructor() public payable {
        paused = false;
        decimals = 0;
    }

    function approve(address _spender, uint256 _value) public {
        require(!paused);
        allowance[msg.sender][_spender] = _value;
    }

    function burn(uint256 _value) public {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        currentsupply -= _value;
    }

    function unpause() public {
        require(paused && msg.sender == owner);
        paused = false;
    }

    function unfreezeAccount(address _target) public {
        require(msg.sender == owner);
        frozenaccount[_target] = false;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(!paused && _value <= allowance[_from][msg.sender]);
        allowance[_from][msg.sender] -= _value;
        require(!frozenaccount[_to] && balanceof[_from] >= _value && _to != address(0) && balanceof[_to] + _value >= balanceof[_to] && !frozenaccount[_from]);
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
    }

    function mintToken(address _target, uint256 _mintedAmount) public {
        require(msg.sender == owner && totalsupply >= currentsupply + _mintedAmount);
        balanceof[_target] += _mintedAmount;
        currentsupply += _mintedAmount;
    }

    function burnFrom(address _from, uint256 _value) public {
        require(balanceof[_from] >= _value && _value <= allowance[_from][msg.sender]);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        currentsupply -= _value;
    }

    function pause() public {
        require(!paused && msg.sender == owner);
        paused = true;
    }

    function freezeAccount(address _target) public {
        require(msg.sender == owner);
        frozenaccount[_target] = true;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner);
        owner = newOwner;
    }

    function transfer(address _to, uint256 _value) public {
        require(balanceof[_to] + _value >= balanceof[_to] && _to != address(0) && !paused && balanceof[msg.sender] >= _value && !frozenaccount[_to] && !frozenaccount[msg.sender]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public {
        require(!paused);
        allowance[msg.sender][_spender] = _value;
        if(_extraData.length >= 0) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,address,bytes)", msg.sender, _value, this, _extraData));
            require(success);
        }
    }
}
