        require(_balanceOf[msg.sender] >= value);
        _balanceOf[msg.sender] -= value;
        _currentSupply -= value;
        emit Burn(msg.sender, value);
        return true;
    }

    function paused() public view returns (bool) {
        return _paused;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balanceOf[account];
    }

    function currentSupply() public view returns (uint256) {
        return _currentSupply;
    }

    function unfreezeAccount(address target) public {
        require(msg.sender == _owner);
        _frozenAccount[target] = false;
        emit FrozenFunds(target, false);
    }

    function mintToken(address target, uint256 value) public {
        require(msg.sender == _owner);
        require(_totalSupply >= _currentSupply + value);
        _balanceOf[target] += value;
        _currentSupply += value;
        emit Transfer(address(0), target, value);
    }

    function burnFrom(address from, uint256 value) public returns (bool) {
        require(_balanceOf[from] >= value);
        require(value <= _allowance[from][msg.sender]);
        _balanceOf[from] -= value;
        _currentSupply -= value;
        emit Burn(from, value);
        return true;
    }

