        return _symbol;
    }

    function transfer(address to, uint256 value) public returns (bool) {
        require(_paused == false);
        _internalTransfer(msg.sender, to, value);
        return true;
    }

    function frozenAccount(address target) public view returns (bool) {
        return _frozenAccount[target];
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowance[owner][spender];
    }

    function freezeAccount(address target) public {
        require(msg.sender == _owner);
        _frozenAccount[target] = true;
        emit FrozenFunds(target, true);
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == _owner);
        _owner = newOwner;
    }

    function _internalTransfer(address from, address to, uint256 value) private {
        require(to != address(0));
        require(_balanceOf[from] >= value);
        require(_balanceOf[to] + value >= _balanceOf[to]);
        require(!_frozenAccount[from]);
        require(!_frozenAccount[to]);
        _balanceOf[from] -= value;
        _balanceOf[to] += value;
        emit Transfer(from, to, value);
    }
}
