```solidity
pragma solidity 0.6.12;

contract YourContract {
    address public owner;
    uint8 public state;
    address payable public wallet;
    mapping(address => uint) public deposited;

    constructor(address payable _wallet) public {
        owner = msg.sender;
        wallet = _wallet;
        state = 0;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function.");
        _;
    }

    function close() external onlyOwner {
        require(state <= 2 && state == 0, "Invalid state");
        state = 2;
        (bool success, ) = wallet.call.value(address(this).balance)("");
        require(success, "Transfer failed.");
    }

    function enableRefunds() external onlyOwner {
        require(state <= 2 && state == 0, "Invalid state");
        state = 1;
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "New owner address cannot be 0");
        owner = newOwner;
    }

    function unhold() external onlyOwner {
        require(state <= 2 && state == 0, "Invalid state");
        (bool success, ) = wallet.call.value(address(this).balance)("");
        require(success, "Transfer failed.");
    }

    function refund(address payable recipient) external {
        require(state <= 2 && state == 1, "Invalid state or not in refund state");
        uint amount = deposited[recipient];
        require(amount > 0, "No funds to refund");
        deposited[recipient] = 0;
        (bool success, ) = recipient.call.value(amount)("");
        require(success, "Refund failed.");
    }

    function deposit(address depositor) external payable onlyOwner {
        require(state <= 2 && state == 0, "Invalid state");
        require(msg.value > 0, "No funds to deposit");
        uint oldDeposit = deposited[depositor];
        uint newDeposit = oldDeposit + msg.value;
        require(newDeposit >= oldDeposit, "Overflow error");
        deposited[depositor] = newDeposit;
    }
}
```

