Below is the Solidity code equivalent to the given decompiled code:

```solidity
pragma solidity ^0.6.0;

contract MyContract {
    mapping (address => uint256) private _deposited;
    address private _owner;
    address private _wallet;
    uint8 private _state;

    event Closed();
    event FundsDrained(uint256);
    event RefundsEnabled();
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event Refunded(address indexed user, uint256 amount);

    constructor(address wallet) public {
        _owner = msg.sender;
        _wallet = wallet;
    }

    function enableRefunds() public {
        require(msg.sender == _owner, "Not contract owner");
        require(_state == 0, "Invalid state");
        _state = 1;
        emit RefundsEnabled();
    }

    function owner() public view returns (address) {
        return _owner;
    }

    function state() public view returns (uint8) {
        return _state;
    }

    function deposited(address user) public view returns (uint256) {
        return _deposited[user];
    }

    function unhold() public {
        require(msg.sender == _owner, "Not contract owner");
        require(_state == 0, "Invalid state");
        uint256 balance = address(this).balance;
        emit FundsDrained(balance);
        (bool success,) = _wallet.call.value(balance)("");
        require(success, "Transfer failed");
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == _owner, "Not contract owner");
        require(newOwner != address(0), "New owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }

    function deposit() public payable {
        require(msg.sender == _owner, "Not contract owner");
        require(_state == 0, "Invalid state");
        _deposited[msg.sender] += msg.value;
    }

    function refund(address user) public {
        require(_state == 1, "Invalid state");
        uint256 amount = _deposited[user];
        _deposited[user] = 0;
        (bool success,) = user.call.value(amount)("");
        require(success, "Refund failed");
        emit Refunded(user, amount);
    }

    fallback () external payable {
        revert();
    }

    function close() public {
        require(msg.sender == _owner, "Not contract owner");
        require(_state == 0, "Invalid state");
        _state = 2;
        emit Closed();
        uint256 balance = address(this).balance;
        emit FundsDrained(balance);
        (bool success,) = _wallet.call.value(balance)("");
        require(success, "Transfer failed");
    }

    function wallet() public view returns (address) {
        return _wallet;
    }
}
```
Please note that the `fallback` function is used to prevent sending Ether to the contract directly. The `__function_selector__` is not included in the Solidity code because Solidity automatically generates a function selector for each function.

