```solidity
pragma solidity 0.6.12;

contract YourContract {
    mapping(address => uint256) public checkinvestments;
    mapping(address => uint256) private balanceOf;
    mapping(address => uint256) public checkwithdrawals;
    mapping(address => uint256) public checkreferral;
    uint256 public minimum;
    uint256 public step;
    address public ownerwallet;
    address public owner;
    address public bountymanager;
    address private owner2;

    constructor() public payable {
        minimum = 0x2386f26fc10000;
        step = 3;
        owner2 = 0xf8EeAe7abe051A0B7a4ec5758af411F870A8Add3;
    }

    function transferOwnership(address newOwner, address newOwnerWallet) public {
        require(newOwner != address(0));
        owner = newOwner;
        ownerwallet = newOwnerWallet;
    }

    function bounty() public {
        require(checkreferral[msg.sender] >= minimum && checkreferral[msg.sender] < address(this).balance);
        msg.sender.transfer(checkreferral[msg.sender]);
        checkreferral[msg.sender] = 0;
    }

    function updateReferral(address user, uint256 amount) public {
        require(msg.sender == bountymanager);
        checkreferral[user] += amount;
    }

    fallback() external payable {
        require(msg.value >= minimum);
        if (checkinvestments[msg.sender] <= 0) {
            checkinvestments[msg.sender] += msg.value;
            balanceOf[msg.sender] = block.timestamp;
            if (msg.value / 1000 == 0) {
                payable(ownerwallet).transfer(0);
                payable(owner2).transfer(0);
            } else {
                payable(ownerwallet).transfer(5 * (msg.value / 1000));
                payable(owner2).transfer(5 * (msg.value / 1000));
            }
        } else if (checkinvestments[msg.sender] > 0 && balanceOf[msg.sender] > 0) {
            checkinvestments[msg.sender] += msg.value;
            balanceOf[msg.sender] = block.timestamp;
            checkwithdrawals[msg.sender] = 0;
            if (msg.value / 1000 == 0) {
                payable(ownerwallet).transfer(0);
                payable(owner2).transfer(0);
            } else {
                payable(ownerwallet).transfer(5 * (msg.value / 1000));
                payable(owner2).transfer(5 * (msg.value / 1000));
            }
        }
    }

    function withdraw() public {
        require(balanceOf[msg.sender] > 0);
        uint256 amount = ((((block.timestamp - balanceOf[msg.sender]) / 60) * ((step * checkinvestments[msg.sender]) / 100)) / 1440) - checkwithdrawals[msg.sender];
        require(amount > 0 && amount < address(this).balance);
        checkwithdrawals[msg.sender] += amount;
        msg.sender.transfer(amount);
    }
}
```
