```solidity
//SPDX-License-Identifier: UNLICENSED
pragma solidity 0.6.12;

contract BettingGame {
    address private owner1;
    address private owner;
    address payable public currentplayer;
    uint256 public currentbet;
    uint256 private price;
    uint256 private totalSupply;
    uint256 public playblocknumber;

    constructor() public {
        owner1 = msg.sender; 
        owner = msg.sender; 
    }

    fallback() external payable {
        if (msg.value <= (address(this).balance / 5) - 1) {
            if (msg.value > 0 && block.number - playblocknumber > 50) {
                if (msg.value == 0) {
                    currentplayer = msg.sender;
                    currentbet = msg.value;
                    playblocknumber = block.number;
                    price += msg.value;
                } else if (currentplayer == address(0) && msg.value != 0) {
                    currentplayer = msg.sender;
                    currentbet = msg.value;
                    playblocknumber = block.number;
                    price += msg.value;
                }
            } else if (block.number - playblocknumber <= 50 && currentplayer != address(0) && block.number > 1 + playblocknumber && msg.value == 0) {
                require(msg.sender != owner1);
                if ((uint256(blockhash(1 + playblocknumber)) % 50) > 0) {
                    currentbet = 0;
                }
                if ((uint256(blockhash(playblocknumber)) & 1) != 1 && uint256(blockhash(1 + playblocknumber)) % 50 > 0) {
                    currentbet = 0;
                }
                currentplayer = address(0);
                currentbet = 0;
                playblocknumber = 0;
                if ((uint256(blockhash(1 + playblocknumber)) % 50) > 0) {
                    if ((uint256(blockhash(playblocknumber)) & 1) == 1 && uint256(blockhash(1 + playblocknumber)) > 0) {
                        totalSupply += currentbet;
                        (bool success, ) = msg.sender.call{value: currentbet * 2}("");
                        require(success, "Transfer failed.");
                        currentbet = 0;
                    }
                }
                currentplayer = address(0);
                playblocknumber = 0;
                if (msg.sender == owner1 && (uint256(blockhash(1 + playblocknumber)) % 50) <= 0) {
                    totalSupply += currentbet;
                    (bool success, ) = msg.sender.call{value: currentbet * 2}("");
                    require(success, "Transfer failed.");
                    currentbet = 0;
                }
                currentplayer = address(0);
                playblocknumber = 0;
            }
        }
    }

    function setCroupier(address newOwner) public {
        require(msg.sender == owner1, "Only owner1 can set new owner");
        owner = newOwner;
    }
}
```
Please note that Solidity is a statically-typed language where the type of each variable (state, local, function parameter) needs to be specified. Also, Solidity uses an Ether subdenomination called 'wei', where 1 ether is equal to 1e18 wei. Therefore, the contract uses wei for all the calculations.
