// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

contract YourContract {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;

    constructor() public payable {
        decimals = 18;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function burn(uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        return true;
    }

    function burnFrom(address _from, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender] && balanceof[_from] >= _value);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        totalsupply -= _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);
        require(balanceof[_from] >= _value && balanceof[_to] + _value > balanceof[_to] && _to != address(0));
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        return true;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value && balanceof[_to] + _value > balanceof[_to] && _to != address(0));
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        if (_extraData.length == 0) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            require(success);
        } else if (_extraData.length <= 32) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            require(success);
        } else if (_extraData.length <= 64) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            require(success);
        } else if (_extraData.length <= 96) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            require(success);
        } else if (_extraData.length <= 128) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            require(success);
        } else if (_extraData.length <= 160 && _extraData.length > 128) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            require(success);
        }
        return true;
    }
}
