```solidity
pragma solidity 0.6.12;

contract Crowdfunding {
    using SafeMath for uint256;

    address payable public beneficiary;
    uint256 public fundinggoal;
    uint256 public amountraised;
    uint256 public deadline;
    uint256 public price;
    address public tokenreward;
    mapping(address => uint256) public balanceof;
    bool private paused;
    bool private paused1;

    constructor(
        address payable _beneficiary,
        uint256 _fundinggoal,
        uint256 _deadline,
        uint256 _price,
        address _tokenreward
    ) public {
        beneficiary = _beneficiary;
        fundinggoal = _fundinggoal;
        deadline = _deadline;
        price = _price;
        tokenreward = _tokenreward;
    }

    fallback() external payable {
        require(!paused, "The contract is paused");
        uint256 amount = msg.value;
        balanceof[msg.sender] = balanceof[msg.sender].add(amount);
        amountraised = amountraised.add(amount);
        if (price != 0) {
            (bool success, ) = tokenreward.call(
                abi.encodeWithSignature("transfer(address,uint256)", msg.sender, amount.mul(0xde0b6b3a7640000).div(price))
            );
            require(success, "Transfer failed");
        }
    }

    function checkGoalReached() public {
        require(block.timestamp >= deadline, "Deadline has not been reached");
        paused = true;
        if (amountraised >= fundinggoal) {
            paused1 = true;
        }
    }

    function safeWithdrawal() public {
        require(block.timestamp >= deadline, "Deadline has not been reached");
        if (!paused1) {
            uint256 amount = balanceof[msg.sender];
            balanceof[msg.sender] = 0;
            if (amount > 0) {
                (bool success, ) = msg.sender.call{value: amount}("");
                if (!success) {
                    balanceof[msg.sender] = amount;
                }
            }
        } else if (msg.sender == beneficiary) {
            (bool success, ) = beneficiary.call{value: amountraised}("");
            if (!success) {
                paused1 = false;
            }
        }
    }
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        uint256 c = a - b;
        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b > 0, "SafeMath: division by zero");
        uint256 c = a / b;
        return c;
    }
}
```
This contract makes use of a SafeMath library to prevent overflows and underflows. The fallback function allows the contract to receive funds and updates the balance of the sender and the total amount raised. The checkGoalReached function checks whether the funding goal has been reached after the deadline and updates the state variables accordingly. The safeWithdrawal function allows users to withdraw their funds after the deadline if the funding goal has not been reached, or allows the beneficiary to withdraw the total amount raised if the funding goal has been achieved.
