Here is the Solidity smart contract that meets your specifications:

```solidity
pragma solidity 0.6.12;

contract Bank {
    address private owner1;
    address private owner;
    address private owner2;
    uint256 public mindeposit;
    mapping(address => uint256) public holders;

    constructor() public payable {
        owner = msg.sender;
        owner2 = msg.sender;
    }

    fallback() external payable {
        if(msg.value > mindeposit) {
            holders[msg.sender] += msg.value;
        }
    }

    function initTokenBank() public {
        owner2 = msg.sender;
        mindeposit = 0xde0b6b3a7640000;
    }

    function changeOwner(address _newOwner) public {
        require(msg.sender == owner, "Only owner can change owner");
        owner1 = _newOwner;
    }

    function confirmOwner() public {
        require(msg.sender == owner1, "Only proposed owner can confirm ownership");
        owner = owner1;
    }

    function deposit() public payable {
        require(msg.value > mindeposit, "Deposit must be greater than mindeposit");
        holders[msg.sender] += msg.value;
    }

    function withdrawToken(address _contract, uint256 _amount, address _to) public {
        require(msg.sender == owner, "Only owner can withdraw tokens");
        bytes memory data = abi.encodeWithSignature("transfer(address,uint256)", _to, _amount);
        _contract.call(data);
    }

    function withdrawTokenToHolder(address _holder, address _contract, uint256 _amount) public {
        require(msg.sender == owner, "Only owner can withdraw tokens to holder");
        require(holders[_holder] > 0, "Holder has no tokens");
        holders[_holder] = 0;
        bytes memory data = abi.encodeWithSignature("transfer(address,uint256)", _holder, _amount);
        _contract.call(data);
    }

    function withdrawToHolder(address payable _holder, uint256 _amount) public payable {
        require(msg.sender == owner, "Only owner can transfer Ether");
        require(holders[_holder] >= _amount, "Insufficient balance for this holder");
        _holder.transfer(_amount);
        holders[_holder] -= _amount;
    }
}
```

Please note that the `withdrawToken` and `withdrawTokenToHolder` functions are simplified and do not handle errors that might occur during the execution of the external call. Depending on the called contract, this might be a problem. You should consider adding error handling to these functions.
