pragma solidity 0.6.12;

contract GameContract {
    address private owner1;
    address payable private owner;
    address payable public currentplayer;
    uint256 public currentbet;
    uint256 private totalSupply1;
    uint256 private totalSupply;
    uint256 public playblocknumber;

    constructor() public {
        owner1 = msg.sender;
    }

    function setCroupier(address _newOwner) external payable {
        require(msg.sender == owner1, "Only owner can set new owner");
        owner = payable(_newOwner);
    }

    fallback() external payable {
        require(msg.value <= ((address(this).balance/5)-1), "Invalid amount");

        if(currentplayer == address(0) && msg.value > 0) {
            currentplayer = msg.sender;
            currentbet = msg.value;
            totalSupply1 += currentbet;
            playblocknumber = block.number;
        } else if(currentplayer != address(0) && block.number > (playblocknumber+1) && msg.value == 0) {
            if((uint(blockhash(playblocknumber+1))%50) <= 0) {
                if(msg.sender != owner) {
                    currentbet = 0;
                } else {
                    totalSupply += currentbet;
                    (bool success,) = owner.call{value: currentbet*2}("");
                    require(success, "Transfer failed");
                    currentbet = 0;
                }
                currentplayer = address(0);
                playblocknumber = 0;
            } else {
                currentplayer = address(0);
                currentbet = 0;
                playblocknumber = 0;

                if((uint(blockhash(playblocknumber+1))%2) != (uint(blockhash(playblocknumber))%2)) {
                    if(msg.sender != owner) {
                        currentbet = 0;
                    } else {
                        totalSupply += currentbet;
                        (bool success,) = owner.call{value: currentbet*2}("");
                        require(success, "Transfer failed");
                        currentbet = 0;
                    }
                    currentplayer = address(0);
                    playblocknumber = 0;
                } else {
                    totalSupply += currentbet;
                    (bool success,) = owner.call{value: currentbet*2}("");
                    require(success, "Transfer failed");
                    currentbet = 0;
                    currentplayer = address(0);
                    playblocknumber = 0;
                }
            }
        }
    }
}
```

