Here is the contract code based on your description. This contract uses Solidity version `0.6.12`.

```solidity
pragma solidity 0.6.12;

contract BountyContract {
    address payable[] private addressOfBountyMembers;
    address public owner;
    address public feeaccount;
    uint256 public makerfee;
    uint256 public takerfee;
    uint256 public inactivityreleaseperiod;
    mapping(address => bool) public approvedcurrencytokens;
    mapping(address => uint256) public invalidorder;
    mapping(address => mapping(address => uint256)) public balanceof;
    mapping(address => bool) public admins;
    mapping(address => uint256) public lastactivetransaction;
    mapping(address => mapping(address => uint256)) public orderfills;
    mapping(bytes32 => uint256) public withdrawn;

    constructor() public {
        owner = msg.sender;
        feeaccount = msg.sender;
        makerfee = 0;
        takerfee = 0;
        inactivityreleaseperiod = 0;
    }

    function setAdmin(address _admin, bool _isAdmin) public {
        require(msg.sender == owner, "Only owner can set admin");
        admins[_admin] = _isAdmin;
    }

    function approveCurrencyTokenAddress(address _token, bool _isApproved) public {
        require(msg.sender == owner || admins[msg.sender], "Only owner or admin can approve token");
        approvedcurrencytokens[_token] = _isApproved;
    }

    function depositToken(address _token, uint256 _amount) public {
        require(balanceof[_token][msg.sender] + _amount >= balanceof[_token][msg.sender], "Overflow error");
        balanceof[_token][msg.sender] += _amount;
        lastactivetransaction[msg.sender] = block.number;
        // Call the transferFrom function of the token contract
        (bool success, ) = _token.call(abi.encodeWithSignature("transferFrom(address,address,uint256)", msg.sender, address(this), _amount));
        require(success, "Transfer not successful");
    }

    function invalidateOrdersBefore(address _user, uint256 _cutoff) public {
        require(msg.sender == owner || admins[msg.sender], "Only owner or admin can invalidate orders");
        require(_cutoff >= invalidorder[_user], "Invalid cutoff");
        invalidorder[_user] = _cutoff;
    }

    function setMakerFee(uint256 _makerfee) public {
        require(msg.sender == owner || (admins[msg.sender] && _makerfee <= 0x2386f26fc10000), "Only owner or admin can set maker fee");
        require(_makerfee != makerfee, "Fee is the same");
        makerfee = _makerfee > 0x2386f26fc10000 ? 0x2386f26fc10000 : _makerfee;
    }

    function receiveTokenDeposit(address _token, address _user, uint256 _amount) public {
        require(balanceof[_token][_user] + _amount >= balanceof[_token][_user], "Overflow error");
        balanceof[_token][_user] += _amount;
        lastactivetransaction[_user] = block.number;
        // Call the transferFrom function of the token contract
        (bool success, ) = _token.call(abi.encodeWithSignature("transferFrom(address,address,uint256)", _user, address(this), _amount));
        require(success, "Transfer not successful");
    }

    function deposit() public payable {
        require(addressOfBountyMembers[msg.sender] + msg.value >= addressOfBountyMembers[msg.sender], "Overflow error");
        addressOfBountyMembers[msg.sender] += msg.value;
        lastactivetransaction[msg.sender] = block.number;
    }

    function setInactivityReleasePeriod(uint256 _inactivityreleaseperiod) public {
        require(msg.sender == owner || (admins[msg.sender] && _inactivityreleaseperiod <= 0xc350), "Only owner or admin can set inactivity release period");
        inactivityreleaseperiod = _inactivityreleaseperiod;
    }

    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner, "Only owner can transfer ownership");
        require(_newOwner != address(0), "New owner cannot be zero address");
        owner = _newOwner;
    }

    function setTakerFee(uint256 _takerfee) public {
        require(msg.sender == owner || (admins[msg.sender] && _takerfee <= 0x470de4df820000), "Only owner or admin can set taker fee");
        require(_takerfee != takerfee, "Fee is the same");
        takerfee = _takerfee > 0x470de4df820000 ? 0x470de4df820000 : _takerfee;
    }

    function withdraw(address _token, uint256 _amount) public {
        require(balanceof[_token][msg.sender] >= _amount, "Insufficient balance");
        require(lastactivetransaction[msg.sender] + inactivityreleaseperiod <= block.number, "Inactivity period not reached");
        balanceof[_token][msg.sender] -= _amount;
        if (_token == address(0)) {
            msg.sender.transfer(_amount);
        } else {
            (bool success, ) = _token.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, _amount));
            require(success, "Transfer not successful");
        }
    }
}
```

This contract defines several functions for managing a smart contract for bounty program. It uses mappings to keep track of various data like admins, balance of tokens for each user, approved tokens, etc. It allows the owner or admins to set maker fee, taker fee, inactivity release period, etc. and approve tokens. It also allows users to deposit and withdraw tokens. The contract also handles ether deposits and transfers.

Please note that this contract is simplified and does not include all the necessary checks and security measures. Always have your smart contracts audited by a professional before deploying them to the mainnet.
