Here is your Solidity contract based on your description:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    uint256 public totalsupply;
    address public owner;
    address public transfer_proxy_vefx;
    address public transfer_proxy_v2;
    mapping (address => bool) public issigner;
    string public name;
    string public symbol;
    uint256 public decimals;
    address public originaltoken;
    mapping (address => uint256) public depositlock;
    mapping (address => uint256) public balanceof;

    constructor() public payable {
        transfer_proxy_vefx = 0xdcDb42C9a256690bd153A7B409751ADFC8Dd5851;
        transfer_proxy_v2 = 0x2240Dab907db71e64d3E0dbA4800c83B5C502d4E;
        originaltoken = address(0);
    }

    function isValidSignature(bytes32 h, uint8 v, bytes32 r, bytes32 s) public view returns (bool) {
        bytes32 hash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", h));
        address signer = ecrecover(hash, v, r, s);
        return issigner[signer];
    }

    function withdraw(uint256 amount, uint8 v, bytes32 r, bytes32 s, uint256 blockNumber) public {
        require(balanceof[msg.sender] >= amount);
        balanceof[msg.sender] -= amount;
        totalsupply -= amount;
        msg.sender.transfer(amount);
        depositlock[msg.sender] = 0;
        if(blockNumber > block.number) {
            bytes32 hash = keccak256(abi.encodePacked(sha256(abi.encodePacked(msg.sender, this, blockNumber)), v, r, s));
            address signer = ecrecover(hash, v, r, s);
            require(issigner[signer]);
            balanceof[msg.sender] -= amount;
        }
    }

    function transferFrom(address from, address to, uint256 value) public {
        require(issigner[to]);
        balanceof[to] += value;
        balanceof[from] -= value;
        if (block.timestamp > depositlock[to]) {
            depositlock[to] = block.timestamp + 3600;
        }
    }

    function addSigner(address newSigner) public {
        require(issigner[msg.sender]);
        issigner[newSigner] = true;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0));
        require(msg.sender == owner);
        owner = newOwner;
    }

    function deposit(uint256 value, uint256 timestamp) public payable {
        require(timestamp >= 1);
        require(block.timestamp + timestamp * 3600 >= depositlock[msg.sender]);
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
        depositlock[msg.sender] = block.timestamp + timestamp * 3600;
    }

    function withdrawDifferentToken(address token, bool isFull) public {
        require(msg.sender == owner);
        ERC20 myToken = ERC20(token);
        uint256 balance = myToken.balanceOf(address(this));
        require(balance > 0);
        if(isFull){
            myToken.transfer(msg.sender, balance);
        }
        else{
            myToken.transfer(msg.sender, balance/2);
        }
    }  
}

interface ERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}
```

Please replace `YourContract` and `ERC20` with your actual contract names. Also, you may need to import `SafeMath` library for safe arithmetic operations.
