pragma solidity ^0.6.12;

contract MyContract {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    function approve(address _spender, uint256 _value) public {
        require(_spender != address(0), "Invalid address");
        allowance[msg.sender][_spender] = _value;
    }

    function decreaseAllowance(address _spender, uint256 _value) public {
        require(_spender != address(0) && _value <= allowance[msg.sender][_spender], "Invalid operation");
        allowance[msg.sender][_spender] -= _value;
    }

    function deposit(address _to) public payable {
        require(_to != address(0) && (msg.value + balanceOf[_to] >= balanceOf[_to]), "Invalid operation");
        balanceOf[_to] += msg.value;
    }

    function transferToCelerWallet(address _from, address payable _to, bytes32 _data, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender], "Invalid operation");
        allowance[_from][msg.sender] -= _value;
        if(_value <= balanceOf[_from]){
            balanceOf[_from] -= _value;
            (bool success,) = _to.call{value: _value}(abi.encodePacked("depositETH", _data));
            require(success, "Transfer failed");
        }
    }

    function increaseAllowance(address _spender, uint256 _value) public {
        require(_value + allowance[msg.sender][_spender] >= allowance[msg.sender][_spender] && _spender != address(0), "Invalid operation");
        allowance[msg.sender][_spender] += _value;
    }

    function withdraw(uint256 _amount) public {
        require(msg.sender != address(0) && _amount <= balanceOf[msg.sender], "Invalid operation");
        balanceOf[msg.sender] -= _amount;
        msg.sender.transfer(_amount);
    }

    function transferFrom(address _from, address payable _to, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender], "Invalid operation");
        allowance[_from][msg.sender] -= _value;
        if(_to != address(0) && _value <= balanceOf[_from]){
            balanceOf[_from] -= _value;
            _to.transfer(_value);
        }
    }
}
```
Please note that this is a simple contract and does not include some advanced security checks like overflow and underflow checks. For production-level contract, you should consider using libraries like `SafeMath` from `OpenZeppelin` for safe arithmetic operations.
