pragma solidity 0.6.12;

contract MyContract {
    address public owner;
    mapping(bytes32 => uint) public swaplist;

    constructor() public payable {
        owner = msg.sender;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0), "New owner cannot be zero address");
        require(msg.sender == owner, "Only owner can call this function");
        owner = newOwner;
    }

    function transferAltChain(bytes32 _id, address payable _to, address _contract, uint256 _value) public {
        require(msg.sender == owner, "Only owner can call this function");
        if (_contract == address(0)) {
            _to.transfer(_value);
        } else {
            (bool success, ) = _contract.call(abi.encodeWithSignature("transfer(address,uint256)", _to, _value));
            require(success, "Transfer failed");
        }
    }

    function withdraw(address payable _to, address _contract, uint256 _value) public {
        require(msg.sender == owner, "Only owner can call this function");
        if (_contract == address(0)) {
            _to.transfer(_value);
        } else {
            (bool success, ) = _contract.call(abi.encodeWithSignature("transfer(address,uint256)", _to, _value));
            require(success, "Transfer failed");
        }
    }

    function swapAltChain(bytes32 _id, address _contract, uint256 _value1, uint256 _value2, bytes memory _data) public payable {
        require(owner == address(0) && _data.length <= 0x100000000 && swaplist[_id] == 0 && _value2 >= block.timestamp && _data.length != 65, "Condition not met");
        swaplist[_id] = 1;
        if (_contract != address(0)) {
            (bool success, ) = _contract.call(abi.encodeWithSignature("transferFrom(address,address,uint256)", msg.sender, address(this), _value1));
            require(success, "TransferFrom failed");
        }
    }

    function swap(bytes32 _id, address _contract1, address payable _contract2, uint256 _value1, uint256 _value2, uint256 _value3, bytes memory _data) public payable {
        require(_value3 >= block.timestamp && owner == address(0) && _data.length <= 0x100000000 && swaplist[_id] == 0 && _data.length != 65 && _contract1 != _contract2, "Condition not met");
        swaplist[_id] = 1;
        if (_contract1 != address(0)) {
            (bool success, ) = _contract1.call(abi.encodeWithSignature("transferFrom(address,address,uint256)", msg.sender, address(this), _value1));
            require(success, "TransferFrom failed");
        }
        if (_contract2 == address(0)) {
            (bool success, ) = address(this).call{value: _value2}("");
            require(success, "Transfer failed");
        } else {
            (bool success, ) = _contract2.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, _value2));
            require(success, "Transfer failed");
        }
    }
}
