pragma solidity 0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";

contract MyContract {
    using SafeMath for uint256;

    address private owner;
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;
    mapping (address => uint8) public blacklist;
    uint256 public totalsupply;
    uint256 public totaldistributed;
    uint256 public totalremaining;
    uint256 public value;
    uint8 public distributionfinished;

    constructor() payable public {
        owner = msg.sender;
        totalsupply = 0x19d971e4fe8401e74000000;
        totaldistributed = 0x7c13bc4b2c133c56000000;
    }

    function approve(address _to, uint256 _value) public returns (bool) {
        require(_value == 0 || allowance[msg.sender][_to] == 0);
        allowance[msg.sender][_to] = _value;
        return true;
    }

    function withdraw() public {
        require(msg.sender == owner);
        msg.sender.transfer(address(this).balance);
    }

    function finishDistribution() public {
        require(msg.sender == owner && distributionfinished == 0);
        distributionfinished = 1;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
        require(_to != address(0));
        require(_value <= balanceOf[_from]);
        require(_value <= allowance[_from][msg.sender]);

        balanceOf[_from] = balanceOf[_from].sub(_value);
        allowance[_from][msg.sender] = allowance[_from][msg.sender].sub(_value);
        balanceOf[_to] = balanceOf[_to].add(_value);
        return true;
    }

    function burn(uint256 _value) public returns (bool success) {
        require(msg.sender == owner);
        require(balanceOf[msg.sender] >= _value); 

        balanceOf[msg.sender] -= _value;
        totalsupply -= _value;
        totaldistributed -= _value;
        return true;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(_to != address(0));
        require(balanceOf[msg.sender] >= _value);

        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        return true;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner);
        require(newOwner != address(0));
        owner = newOwner;
    }

    fallback() external payable {
        require(blacklist[msg.sender] == 0 && distributionfinished == 0);
