Based on the decompiled EVM bytecode, the Solidity smart contract might look like this:

```solidity
pragma solidity ^0.6.0;

contract MyToken {
    mapping (address => uint256) private _balanceOf;
    mapping (address => mapping(address => uint256)) private _allowance;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint256 private _price;
    uint8 private _decimals;
    bool private _initContract;

    event Transfer(address indexed from, address indexed to, uint256 value);

    constructor() public {
        _initContract = true;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function approve(address spender, uint256 value) public returns (bool) {
        _allowance[msg.sender][spender] = value;
        return true;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function transferFrom(address from, address to, uint256 value) public returns (bool) {
        require(to != address(0), "ERC20: transfer to the zero address");
        require(value <= _balanceOf[from], "ERC20: transfer amount exceeds balance");
        require(value <= _allowance[from][msg.sender], "ERC20: transfer amount exceeds allowance");

        _balanceOf[from] -= value;
        _balanceOf[to] += value;
        _allowance[from][msg.sender] -= value;
        emit Transfer(from, to, value);
        return true;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balanceOf[account];
    }

    function initContract() public {
        require(_initContract, "Contract is already initialized");
        _totalSupply = 1000000;
        _balanceOf[msg.sender] = _totalSupply;
        emit Transfer(address(this), msg.sender, _totalSupply);
        _initContract = false;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function price() public view returns (uint256) {
        return _price;
    }

    function transfer(address to, uint256 value) public returns (bool) {
        require(to != address(0), "ERC20: transfer to the zero address");
        require(value <= _balanceOf[msg.sender], "ERC20: transfer amount exceeds balance");

        _balanceOf[msg.sender] -= value;
        _balanceOf[to] += value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowance[owner][spender];
    }

    fallback() external payable {
        require(msg.value > 0, "Value must be greater than 0");
        uint256 tokens = msg.value * _price;
        require(tokens/msg.value == _price, "Overflow error");
        _balanceOf[msg.sender] += tokens;
        emit Transfer(address(this), msg.sender, tokens);
    }
}
```
