pragma solidity 0.6.12;

contract YourContract {
  bool public paused;
  address public owner;
  uint256 public totalsupply;
  mapping(address => uint256) public balanceof;
  mapping(address => mapping(address => uint256)) public allowance;
  uint256 public investtotal;
  uint256 public investether;
  uint256 public investmin;
  uint256 public investratio;
  uint256 public investbegintime;
  uint256 public investendtime;
  address payable public investholder;

  constructor() public payable {
    totalsupply = 0;
    investtotal = 0;
    investether = 0;
    investmin = 0;
    investratio = 0;
    investbegintime = 0x5cab3cf2;
    investendtime = 0x5cab3cf2;
    investholder = 0xbCADE28d8C2F22345165f0e07C94A600f6C4e925;
  }

  function approve(address _spender, uint256 _value) public {
    require(!paused && _spender != address(0));
    allowance[msg.sender][_spender] = _value;
  }

  function withdraw() public {
    uint256 balance = address(this).balance;
    investholder.transfer(balance);
  }

  function unpause() public {
    require(owner == msg.sender && paused);
    paused = false;
  }

  function increaseAllowance(address _spender, uint256 _addedValue) public {
    require(!paused && _spender != address(0));
    require(allowance[msg.sender][_spender] + _addedValue >= allowance[msg.sender][_spender]);
    allowance[msg.sender][_spender] += _addedValue;
  }

  function renounceOwnership() public {
    require(owner == msg.sender);
    owner = address(0);
  }

  function transferFrom(address _from, address _to, uint256 _value) public {
    require(!paused && _value <= allowance[_from][msg.sender]);
    allowance[_from][msg.sender] -= _value;
    require(_to != address(0) && _value <= balanceof[_from]);
    balanceof[_from] -= _value;
    require(_value + balanceof[_to] >= balanceof[_to]);
    balanceof[_to] += _value;
  }

  function burn(uint256 _value) public {
    require(!paused && _value <= balanceof[msg.sender]);
    balanceof[msg.sender] -= _value;
    require(_value <= totalsupply);
    totalsupply -= _value;
  }

  fallback() external payable {
    require((msg.value*investratio)/0xde0b6b3a7640000 > 0 && block.timestamp > investbegintime && msg.value >= investmin && block.timestamp < investendtime && ((msg.value*investratio)/0xde0b6b3a7640000) + investtotal >= investtotal);
    investtotal += (msg.value*investratio)/0xde0b6b3a7640000;
    require((msg.value + investether) >= investether);
    investether += msg.value;
    require(msg.sender != address(0) && ((msg.value*investratio)/0xde0b6b3a7640000) + totalsupply >= totalsupply);
    totalsupply += (msg.value*investratio)/0xde0b6b3a7640000;
    require(0x5af3107a4000 >= ((msg.value*investratio)/0xde0b6b3a7640000) + totalsupply && ((msg.value*investratio)/0xde0b6b3a7640000) + balanceof[msg.sender] >= balanceof[msg.sender]);
    balanceof[msg.sender] += (msg.value*investratio)/0xde0b6b3a7640000;
  }

  function pause() public {
    require(!paused && owner == msg.sender);
    paused = true;
  }

  function changeInvestSetting(uint256 _investmin, uint256 _investratio, uint256 _investbegintime, uint256 _investendtime, address payable _investholder) public {
    require(owner == msg.sender && _investratio <= 0x3b9ac9ff);
    investmin = _investmin;
    investratio = _investratio;
    investbegintime = _investbegintime;
    investendtime = _investendtime;
    investholder = _investholder;
  }

  function decreaseAllowance(address _spender, uint256 _subtractedValue) public {
    require(!paused && _spender != address(0) && _subtractedValue <= allowance[msg.sender][_spender]);
    allowance[msg.sender][_spender] -= _subtractedValue;
  }

  function burnFrom(address _from, uint256 _value) public {
    require(!paused && _value <= allowance[_from][msg.sender]);
    allowance[_from][msg.sender] -= _value;
    require(_value <= balanceof[_from]);
    balanceof[_from] -= _value;
    require(_value <= totalsupply);
    totalsupply -= _value;
  }

  function transferOwnership(address _newOwner) public {
    require(owner == msg.sender && _newOwner != address(0));
    owner = _newOwner;
  }

  function transfer(address _to, uint256 _value) public {
    require(!paused && _to != address(0) && _value <= balanceof[msg.sender]);
    balanceof[msg.sender] -= _value;
    require(_value + balanceof[_to] >= balanceof[_to]);
    balanceof[_to] += _value;
  }

  function invest() public payable {
    require((msg.value*investratio)/0xde0b6b3a7640000 > 0 && block.timestamp > investbegintime && msg.value >= investmin && block.timestamp < investendtime && ((msg.value*investratio)/0xde0b6b3a7640000) + investtotal >= investtotal);
    investtotal += (msg.value*investratio)/0xde0b6b3a7640000;
    require((msg.value + investether) >= investether);
    investether += msg.value;
    require(msg.sender != address(0) && ((msg.value*investratio)/0xde0b6b3a7640000) + totalsupply >= totalsupply);
    totalsupply += (msg.value*investratio)/0xde0b6b3a7640000;
    require(0x5af3107a4000 >= ((msg.value*investratio)/0xde0b6b3a7640000) + totalsupply && ((msg.value*investratio)/0xde0b6b3a7640000) + balanceof[msg.sender] >= balanceof[msg.sender]);
    balanceof[msg.sender] += (msg.value*investratio)/0xde0b6b3a7640000;
  }
}
