pragma solidity 0.6.12;

contract YourContract {
    address public authority;
    address payable public owner;
    uint256 public nameprice;
    mapping(address => uint256) private balanceOf;
    mapping(address => uint256) private balanceOf1;

    constructor() payable public {
        nameprice = 0x2386f26fc10000;
        owner = msg.sender;
    }

    function setOwner(address newOwner) public {
        require(newOwner != address(0), "New owner address can't be zero");
        if (msg.sender == address(this)) {
            owner = payable(newOwner);
        } else if (msg.sender == owner) {
            owner = payable(newOwner);
        } else if (authority != address(0)) {
            (bool success, bytes memory data) = authority.call(abi.encodeWithSignature("canCall(address,address,uint256,address,uint256)", msg.sender, address(this), 0xffffffff00000000000000000000000000000000000000000000000000000000, newOwner, 0xffffffff00000000000000000000000000000000000000000000000000000000));
            require(success, "External call failed");
            bool v0 = abi.decode(data, (bool));
            if (v0) {
                owner = payable(newOwner);
            }
        }
    }

    function setAuthority(address newAuthority) public {
        require(newAuthority != address(0), "New authority address can't be zero");
        if (msg.sender == address(this)) {
            authority = newAuthority;
        } else if (msg.sender == owner) {
            authority = newAuthority;
        } else if (authority != address(0)) {
            (bool success, bytes memory data) = authority.call(abi.encodeWithSignature("canCall(address,address,uint256,address,uint256)", msg.sender, address(this), 0xffffffff00000000000000000000000000000000000000000000000000000000, newAuthority, 0xffffffff00000000000000000000000000000000000000000000000000000000));
            require(success, "External call failed");
            bool v0 = abi.decode(data, (bool));
            if (v0) {
                authority = newAuthority;
            }
        }
    }

    function getMoney() public {
        if (msg.sender == address(this)) {
            owner.transfer(address(this).balance);
        } else if (msg.sender == owner) {
            owner.transfer(address(this).balance);
        } else if (authority != address(0)) {
            (bool success, bytes memory data) = authority.call(abi.encodeWithSignature("canCall(address,address,uint256,address,uint256)", msg.sender, address(this), 0xffffffff00000000000000000000000000000000000000000000000000000000, owner, 0xffffffff00000000000000000000000000000000000000000000000000000000));
            require(success, "External call failed");
            bool v0 = abi.decode(data, (bool));
            if (v0) {
                owner.transfer(address(this).balance);
            }
        }
    }
}
