pragma solidity 0.6.12;

contract YourContract {
    address public admin;
    address public feeaccount;
    address public accountlevelsaddr;
    uint256 public feemake;
    uint256 public feetake;
    uint256 public feerebate;
    mapping (address => mapping (address => uint256)) public balanceof;
    mapping (address => mapping (bytes32 => uint256)) public orders;
    mapping (address => mapping (bytes32 => uint256)) public orderfills;

    function order(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
        orders[msg.sender][v0] = 1;
    }

    function cancelOrder(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6, uint8 arg7, bytes32 arg8, bytes32 arg9) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
        if (orders[msg.sender][v0] != 0) {
            orderfills[msg.sender][v0] = arg2;
        } else {
            address v1 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", v0)), arg7, arg8, arg9);
            orderfills[msg.sender][v0] = arg2;
        }
    }

    function amountFilled(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6, address arg7, uint8 arg8, bytes32 arg9, bytes32 arg10) public {
        sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
    }

    function changeFeeMake(uint256 _fee) public {
        require(msg.sender == admin, "Only admin can change the fee");
        feemake = _fee;
    }

    function withdraw(uint256 amount) public {
        require(balanceof[address(0)][msg.sender] >= amount, "Insufficient balance");
        balanceof[address(0)][msg.sender] -= amount;
        msg.sender.transfer(amount);
    }

    function changeFeeRebate(uint256 _fee) public {
        require(msg.sender == admin, "Only admin can change the fee");
        feerebate = _fee;
    }

    function changeFeeAccount(address _feeaccount) public {
        require(msg.sender == admin, "Only admin can change the fee account");
        feeaccount = _feeaccount;
    }

    function depositToken(address token, uint256 amount) public {
        require(token != address(0), "Token address cannot be 0");
        (bool success, bytes memory data) = token.call(abi.encodeWithSignature("transferFrom(address,address,uint256)", msg.sender, address(this), amount));
        require(success, "Transfer failed");
        balanceof[token][msg.sender] += amount;
    }

    function changeFeeTake(uint256 _fee) public {
        require(msg.sender == admin, "Only admin can change the fee");
        feetake = _fee;
    }

    function changeAdmin(address _admin) public {
        require(msg.sender == admin, "Only admin can change the admin address");
        admin = _admin;
    }

    function changeAccountLevelsAddr(address _addr) public {
        require(msg.sender == admin, "Only admin can change the account levels address");
        accountlevelsaddr = _addr;
    }

    function deposit() public payable {
        balanceof[address(0)][msg.sender] += msg.value;
    }

    function withdrawToken(address token, uint256 amount) public {
        require(token != address(0), "Token address cannot be 0");
        require(balanceof[token][msg.sender] >= amount, "Insufficient balance");
        balanceof[token][msg.sender] -= amount;
        (bool success, bytes memory data) = token.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, amount));
        require(success, "Transfer failed");
    }
}
```
