```solidity
pragma solidity 0.6.12;

contract Fundraiser {

    address payable public beneficiary;
    uint public fundingGoal;
    uint public amountRaised;
    uint public deadline;
    uint public price;
    address public tokenReward;
    mapping(address => uint) public balanceOf;
    bool private paused = false;
    bool private paused1 = false;

    constructor() public payable {
        beneficiary = msg.sender;
        fundingGoal = 0;
    }

    fallback() external payable {
        require(!paused, "Contract is paused");
        balanceOf[msg.sender] += msg.value;
        amountRaised += msg.value;
        if (price != 0) {
            (bool success,) = tokenReward.call(abi.encodeWithSignature("transfer(address,uint256)", msg.sender, (msg.value * 0xde0b6b3a7640000) / price));
            require(success, "Transfer failed");
        }
    }

    function checkGoalReached() public {
        require(block.timestamp > deadline, "Deadline not reached");
        paused = true;
        if (amountRaised >= fundingGoal) {
            paused1 = true;
        }
    }

    function safeWithdrawal() public {
        require(block.timestamp > deadline, "Deadline not reached");
        if (!paused1) {
            uint amount = balanceOf[msg.sender];
            balanceOf[msg.sender] = 0;
            if (amount > 0) {
                (bool success,) = msg.sender.call{value: amount}("");
                require(success, "Withdrawal failed");
            }
        } else if (paused1 && msg.sender == beneficiary) {
            (bool success,) = beneficiary.call{value: amountRaised}("");
            require(success, "Withdrawal failed");
            paused1 = false;
        }
    }
}
```
Please note that this is a simple implementation of your description. There may be some additional checks and security measures necessary depending on the specific use case.
