pragma solidity 0.6.12;

contract YourContract {
    uint256 public totalsupply;
    address public owner;
    address public transfer_proxy_vefx;
    address public transfer_proxy_v2;
    mapping(address => uint256) public issigner;
    string public name;
    string public symbol;
    uint256 public decimals;
    address public originaltoken;
    mapping(address => uint256) public depositlock;
    mapping(address => uint256) public balanceof;

    constructor() public payable {
        transfer_proxy_vefx = 0xdcDb42C9a256690bd153A7B409751ADFC8Dd5851;
        transfer_proxy_v2 = 0x95E6F48254609A6ee006F7D493c8e5fB97094ceF;
        originaltoken = 0x0;
    }

    function isValidSignature(bytes32 arg1, uint8 arg2, bytes32 arg3, bytes32 arg4) public view returns (address) {
        return ecrecover(keccak256(abi.encodePacked('0x19457468657265756d205369676e6564204d6573736167653a0a3332', arg1)), arg2, arg3, arg4);
    }

    function withdraw(uint256 arg1, uint8 arg2, bytes32 arg3, bytes32 arg4, uint256 arg5) public payable {
        require(balanceof[msg.sender] >= arg1);
        if (block.timestamp > depositlock[msg.sender]) {
            balanceof[msg.sender] -= arg1;
            totalsupply -= arg1;
            msg.sender.transfer(arg1);
            depositlock[msg.sender] = 0;
        }
        if (block.timestamp < depositlock[msg.sender] && arg5 > block.number) {
            address v0 = ecrecover(keccak256(abi.encodePacked('0x19457468657265756d205369676e6564204d6573736167653a0a3332', keccak256(abi.encodePacked(msg.sender, this, arg5, arg2, arg3, arg4)))));
            if (issigner[v0] != 0) {
                balanceof[msg.sender] -= arg1;
            }
        }
    }

    function transferFrom(address arg1, address arg2, uint256 arg3) public {
        if (issigner[arg2] != 0) {
            balanceof[arg2] += arg3;
            balanceof[arg1] -= arg3;
            if (block.timestamp > depositlock[arg2]) {
                depositlock[arg2] = block.timestamp + 3600;
            }
        }
        if (issigner[arg1] != 0 && issigner[arg2] == 0) {
            balanceof[arg2] += arg3;
            balanceof[arg1] -= arg3;
            if (block.timestamp > depositlock[arg2]) {
                depositlock[arg2] = block.timestamp + 3600;
            }
        }
    }

    function addSigner(address arg) public {
        require(issigner[msg.sender] != 0);
        issigner[arg] = 1;
    }

    function transferOwnership(address arg) public {
        require(arg != address(0) && msg.sender == owner);
        owner = arg;
    }

    function deposit(uint256 arg1, uint256 arg2) public payable {
        require(arg2 >= 1 && block.timestamp + arg2 * 3600 >= depositlock[msg.sender]);
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
        depositlock[msg.sender] = block.timestamp + arg2 * 3600;
    }

    function withdrawDifferentToken(address arg) public {
        require(originaltoken != arg && msg.sender == owner);
        uint256 v0 = arg.balanceOf(address(this));
        if (v0 > 0) {
            arg.transfer(owner, v0);
        }
    }
}
