pragma solidity 0.6.12;

contract BountyContract {
    address[] private addressOfBountyMembers;
    uint256 private totalSupply;
    uint256 private totalSupply1;
    uint256 private totalSupply2;
    address public _last_create_address;

    function managerSet() public {
        require(msg.sender != address(0), 'Caller address is zero');
        for (uint256 i = 0; i < addressOfBountyMembers.length; i++) {
            if (addressOfBountyMembers[i] != address(0)) {
                addressOfBountyMembers[i] = address(0);
            }
            addressOfBountyMembers[i] = address(1);
            addressOfBountyMembers[i] = address(totalSupply);
        }
    }

    function managerPropose(address _address) public {
        require(msg.sender != address(0), 'Caller address is zero');
        for (uint256 i = 0; i < addressOfBountyMembers.length; i++) {
            if (addressOfBountyMembers[i] != address(0)) {
                totalSupply = uint256(_address);
            }
        }
    }

    function getCapital(address _address, uint256 _value) public payable {
        require(msg.sender != address(0), 'Caller address is zero');
        for (uint256 i = 0; i < addressOfBountyMembers.length; i++) {
            if (addressOfBountyMembers[i] != address(0)) {
                msg.sender.transfer(_value);
                if (_address != address(0)) {
                    (bool success, ) = _address.call{value: _value}("");
                    require(success, 'Transfer failed');
                }
            }
        }
    }
}
