Here is the contract code based on your requirement:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    uint256 public house_edge;
    uint256 public jackpot;
    address public lastwinner;
    uint256 public last_win_wei;
    uint256 public totalwins;
    uint256 public total_wins_count;
    address private owner;
    bool private decimals;
    uint256 public totaltickets;

    constructor() public payable {
        house_edge = 0;
        jackpot = 0;
        last_win_wei = 0;
        totalwins = 0;
        total_wins_count = 0;
        decimals = true;
        owner = 0x5Bf066c70C2B5e02F1C6723E72e82478Fec41201;
        totaltickets = 0;
    }

    fallback() external payable {
        require(decimals == true && msg.value/0x38d7ea4c68000 == 9);
        jackpot += ((msg.value*98)/100);
        house_edge += (msg.value/100);
        if (msg.sender != owner) {
            totaltickets += 1;
            if (totaltickets % 999 == 0) {
                lastwinner = msg.sender;
                last_win_wei = 0;
                total_wins_count += 1;
                totalwins += ((jackpot*80)/100);
                (bool success, ) = msg.sender.call{value: (jackpot*80)/100}("");
                require(success, "Transfer failed.");
                jackpot -= ((jackpot*80)/100);
            } else if (uint256(keccak256(abi.encodePacked(block.number+totaltickets, blockhash(block.number)))) % 3 == 0) {
                lastwinner = msg.sender;
                last_win_wei = ((50*jackpot)/100);
                total_wins_count += 1;
                totalwins += last_win_wei;
                if (address(this).balance - house_edge < last_win_wei) {
                  last_win_wei = (50*(address(this).balance - house_edge))/100;
                  totalwins += last_win_wei;
                }
                (bool success, ) = msg.sender.call{value: last_win_wei}("");
                require(success, "Transfer failed.");
                jackpot -= last_win_wei;
            }
        }
    }

    function stopGame() public {
        require(msg.sender == owner, "Only the owner can stop the game.");
        decimals = false;
    }

    function startGame() public {
        require(msg.sender == owner, "Only the owner can start the game.");
        decimals = true;
    }

    function transferHouseEdge(uint256 amount) public payable {
        require(msg.sender == owner && address(this).balance - amount > 0 && amount <= house_edge, "Transfer not allowed.");
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed.");
        house_edge -= amount;
    }
}
```

This contract includes a fallback function to handle transactions, a constructor to initialize state variables, and functions for the game owner to stop and start the game and to transfer the house edge. It also includes condition checks and updates for the jackpot and winner logic.
