```solidity
pragma solidity >=0.6.12 <0.7.0;

contract BettingGame {
    address public owner;
    address public pendingowner;
    mapping(uint256 => address) public winner;
    mapping(uint256 => uint256) public balances;
    uint256 private price = 0x38d7ea4c68000;
    uint256 private deadline = 0x5c7d1340;
    uint256 private totalSupply = 3600;
    address payable public wallet;
    address payable public jackpot;

    constructor() payable public {
        owner = msg.sender;
    }

    function claimOwnership() public {
        require(msg.sender == pendingowner);
        owner = pendingowner;
        pendingowner = address(0);
    }

    function changeStartTime(uint256 newStartTime) public {
        require(msg.sender == owner);
        deadline = newStartTime;
    }

    function changeJackpot(address payable newJackpot) public {
        require(msg.sender == owner);
        jackpot = newJackpot;
    }

    function changeWallet(address payable newWallet) public {
        require(msg.sender == owner);
        wallet = newWallet;
    }

    function changeRoundTime(uint256 newRoundTime) public {
        require(msg.sender == owner);
        totalSupply = newRoundTime;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner);
        pendingowner = newOwner;
    }

    function getGain(uint256 betIndex) public {
        require(msg.sender == winner[betIndex] || (msg.sender == owner && winner[betIndex] == address(0)));
        require(betIndex < ((block.timestamp - deadline) / totalSupply));
        uint256 amount = balances[betIndex];
        balances[betIndex] = 0;
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success);
    }

    fallback() external payable {
        require(msg.value >= price);
        uint256 betIndex = (block.timestamp - deadline) / totalSupply;
        winner[betIndex] = msg.sender;
        if (msg.value == 0) {
            balances[betIndex] += msg.value;
        } else {
            uint256 jackpotAmount = (msg.value * 15) / 100;
            uint256 walletAmount = (msg.value * 20) / 100;
            balances[betIndex] += msg.value - jackpotAmount - walletAmount;
            balances[betIndex + 1] += jackpotAmount;
            (bool successJackpot, ) = jackpot.call{value: jackpotAmount}("");
            require(successJackpot);
            (bool successWallet, ) = wallet.call{value: walletAmount}("");
            require(successWallet);
        }
    }

    function setBet(address bettor) external payable {
        require(msg.value >= price);
        uint256 betIndex = (block.timestamp - deadline) / totalSupply;
        winner[betIndex] = bettor;
        if (msg.value == 0) {
            balances[betIndex] += msg.value;
        } else {
            uint256 jackpotAmount = (msg.value * 15) / 100;
            uint256 walletAmount = (msg.value * 20) / 100;
            balances[betIndex] += msg.value - jackpotAmount - walletAmount;
            balances[betIndex + 1] += jackpotAmount;
            (bool successJackpot, ) = jackpot.call{value: jackpotAmount}("");
            require(successJackpot);
            (bool successWallet, ) = wallet.call{value: walletAmount}("");
            require(successWallet);
        }
    }
}
```

Please note that:

- This contract has not been tested. Please run tests before using it.
- This contract does not implement any overflow or underflow checks. Consider using a library like SafeMath for this.
- This contract does not check if the provided address is non-zero before transferring ether to it.
- This contract uses the `call` function to transfer ether, which can potentially enable reentrancy attacks. Use the Checks-Effects-Interactions pattern to prevent this.
- This contract does not emit any events. Consider emitting events for important state changes.
