```solidity
pragma solidity ^0.8.0;

contract ExchangeContract {
    uint256 private _exchangeLimit;
    address private _updateExchangeLimit;
    address private _tokenAddress;
    uint16 private _exchangeRate;

    event TokenExchanged(address indexed user, uint256 amount, uint256 tokens);
    event NewOwner(address indexed oldOwner, address indexed newOwner);

    constructor(address tokenAddress) {
        _tokenAddress = tokenAddress;
        _updateExchangeLimit = msg.sender;
    }

    function setOwner(address newOwner) public {
        require(msg.sender == _updateExchangeLimit, "Not authorized");
        emit NewOwner(_updateExchangeLimit, newOwner);
        _updateExchangeLimit = newOwner;
    }

    function exchangeRate() public view returns (uint16) {
        return _exchangeRate;
    }

    function withdrawEther(uint256 amount) public {
        require(msg.sender == _updateExchangeLimit, "Not authorized");
        (bool success, ) = _updateExchangeLimit.call{value: amount}("");
        require(success, "Withdrawal failed");
    }

    // Assuming that the token contract complies with the ERC20 standard
    function withdrawToken(uint256 amount) public {
        require(msg.sender == _updateExchangeLimit, "Not authorized");
        IERC20 token = IERC20(_tokenAddress);
        require(token.balanceOf(address(this)) >= amount, "Not enough tokens");
        token.transfer(_updateExchangeLimit, amount);
    }

    function updateExchangeLimit(uint256 newLimit) public {
        require(msg.sender == _updateExchangeLimit, "Not authorized");
        _exchangeLimit = newLimit;
    }

    function getTokenBalance() public view returns (uint256) {
        IERC20 token = IERC20(_tokenAddress);
        return token.balanceOf(address(this));
    }

    function owner() public view returns (address) {
        return _updateExchangeLimit;
    }

    function tokenAddress() public view returns (address) {
        return _tokenAddress;
    }

    function exchangeLimit() public view returns (uint256) {
        return _exchangeLimit;
    }

    function updateExchangeRate(uint16 newRate) public {
        require(msg.sender == _updateExchangeLimit, "Not authorized");
        _exchangeRate = newRate;
    }

    function getEtherBalance() public view returns (uint256) {
        return address(this).balance;
    }

    // Assuming that the token contract complies with the ERC20 standard
    function exchangeEtherForTokens() public payable {
        require(msg.value <= _exchangeLimit, "Amount exceeds exchange limit");
        uint256 tokens = _exchangeRate * msg.value;
        IERC20 token = IERC20(_tokenAddress);
        require(token.balanceOf(address(this)) >= tokens, "Not enough tokens");
        token.transfer(msg.sender, tokens);
        emit TokenExchanged(msg.sender, msg.value, tokens);
    }

    fallback() external payable {
        exchangeEtherForTokens();
    }
}

interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}
```

