```solidity
pragma solidity ^0.6.12;

contract MyContract {
    address private owner;
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;
    mapping (address => uint8) public blacklist;
    uint256 public totalsupply;
    uint256 public totaldistributed;
    uint256 public totalremaining;
    uint256 public value;
    uint8 public distributionfinished;

    constructor() public payable {
        owner = msg.sender;
        totalsupply = 0xadb53acfa41aee12000000;
        totaldistributed = 0xa56fa5b99019a5c8000000;
    }

    function approve(address _spender, uint256 _value) public {
        if (_value == 0) {
            allowance[msg.sender][_spender] = _value;
        } else if (allowance[msg.sender][_spender] == 0 && _value != 0) {
            allowance[msg.sender][_spender] = _value;
        }
    }

    function withdraw() public {
        require(msg.sender == owner);
        msg.sender.transfer(address(this).balance);
    }

    function finishDistribution() public {
        require(distributionfinished == 0 && msg.sender == owner);
        distributionfinished = 1;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= balanceOf[_from]);
        require(_value <= allowance[_from][msg.sender]);
        require(_to != address(0));

        balanceOf[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function getTokenBalance(address _tokenAddress, address _who) public view returns (uint256 balance) {
        return ERC20(_tokenAddress).balanceOf(_who);
    }

    function burn(uint256 _value) public {
        require(_value <= balanceOf[msg.sender]);
        require(msg.sender == owner);

        balanceOf[msg.sender] -= _value;
        totalsupply -= _value;
        totaldistributed -= _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0));
        require(_value <= balanceOf[msg.sender]);

        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0));
        require(msg.sender == owner);

        owner = newOwner;
    }

    function withdrawForeignTokens(address _tokenContract) public returns (bool _sent){
        require(msg.sender == owner);
        uint256 amount = ERC20(_tokenContract).balanceOf(address(this));
        return ERC20(_tokenContract).transfer(owner, amount);
    }

    fallback() external payable {
        require(distributionfinished == 0);
        require(blacklist[msg.sender] == 0);

        if (msg.value <= totalremaining) {
            totaldistributed += msg.value;
            totalremaining -= msg.value;
            balanceOf[msg.sender] += msg.value;

            if ((msg.value / 0x186a0) == 0) {
                value = 0x1869f * (msg.value / 0x186a0);
            }

            if (msg.value > 0) {
                blacklist[msg.sender] = 1;
            }

            if (totaldistributed >= totalsupply) {
                distributionfinished = 1;
            }

            if ((0x1869f * (msg.value / 0x186a0)) / (msg.value / 0x186a0) == 0x1869f) {
                value = 0x1869f * (msg.value / 0x186a0);
            }
        } else {
            value = totalremaining;
        }
    }

    function getTokens() public payable {
        require(distributionfinished == 0);
        require(blacklist[msg.sender] == 0);

        if (msg.value <= totalremaining) {
            totaldistributed += msg.value;
            totalremaining -= msg.value;
            balanceOf[msg.sender] += msg.value;

            if ((msg.value / 0x186a0) == 0) {
                value = 0x1869f * (msg.value / 0x186a0);
            }

            if (msg.value > 0) {
                blacklist[msg.sender] = 1;
            }

            if (totaldistributed >= totalsupply) {
                distributionfinished = 1;
            }

            if ((0x1869f * (msg.value / 0x186a0)) / (msg.value / 0x186a0) == 0x1869f) {
                value = 0x1869f * (msg.value / 0x186a0);
            }
        } else {
            value = totalremaining;
        }
    }
}

interface ERC20 {
    function balanceOf(address _owner) external view returns (uint256 balance);
    function transfer(address _to, uint256 _value) external returns (bool success);
}
```

This contract code is written in Solidity version 0.6.12 as per the given specifications. The contract includes a constructor that sets the initial state variables, as well as functions for approving token transactions, withdrawing ether, finishing distribution, transferring tokens from one address to another, getting token balances, burning tokens, transferring ownership of the contract, withdrawing foreign tokens, and a fallback function for handling incoming transactions. It also includes an interface to interact with ERC20 tokens.
