pragma solidity 0.6.12;

contract Crowdfunding {
    using SafeMath for uint256;

    address payable public beneficiary;
    uint256 public fundinggoal;
    uint256 public amountraised;
    uint256 public deadline;
    uint256 public price;
    address public tokenreward;
    mapping(address => uint256) public balanceof;
    bool private paused;
    bool private paused1;

    modifier afterDeadline() { 
        require(block.timestamp >= deadline); 
        _; 
    }

    constructor(
        address payable _beneficiary,
        uint256 _fundinggoal,
        uint256 _duration,
        uint256 _price,
        address _tokenreward
    ) public {
        beneficiary = _beneficiary;
        fundinggoal = _fundinggoal;
        deadline = block.timestamp.add(_duration);
        price = _price;
        tokenreward = _tokenreward;
    }

    receive () external payable {
        require(!paused);
        uint256 amount = msg.value;
        balanceof[msg.sender] = balanceof[msg.sender].add(amount);
        amountraised = amountraised.add(amount);
        if (price != 0) {
            ERC20(tokenreward).transfer(msg.sender, amount.div(price));
        }
    }

    function checkGoalReached() public afterDeadline {
        paused = true;
        if (amountraised >= fundinggoal){
            paused1 = true;
        }
    }

    function safeWithdrawal() public afterDeadline {
        if (!paused1) {
            uint256 amount = balanceof[msg.sender];
            balanceof[msg.sender] = 0;
            if (amount > 0) {
                if (!msg.sender.send(amount)) {
                    balanceof[msg.sender] = amount;
                }
            }
        }

        if (beneficiary == msg.sender && paused1) {
            if(!beneficiary.send(amountraised)) {
                paused1 = false;
            }
        }
    }
}

interface ERC20 {
    function transfer(address to, uint256 value) external returns (bool);
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }

    function sub(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;

        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }

    function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;

        return c;
    }
}
```

This contract is a simple crowdfunding contract where people can send Ether to it and after a certain deadline, if the funding goal has been reached, the beneficiary can withdraw the funds, otherwise the contributors can get their money back.
