```solidity
pragma solidity 0.6.12;

contract MyContract {
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) public ethbalance;
    uint256 public totalsupply;
    uint8 public decimals;
    address payable public contract_owner_address;
    uint256 public start_sale;
    uint256 public presaleperiod;
    address public affiliateaddress;
    uint256 public maxamountpresale_usd;
    uint256 public soldamount_usd;

    constructor() payable public {
        decimals = 0;
        start_sale = 0x5bab4a90;
        presaleperiod = 61;
        maxamountpresale_usd = 0x2625a00;
        soldamount_usd = 0;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function depositContract(uint256 _amount) payable public {
        allowance[msg.sender][address(this)] = _amount;
        address(this).call.value(_amount)("");
    }

    function burn(uint256 _value) public {
        require(_value <= balanceof[msg.sender]);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= balanceof[_from] && _value <= allowance[_from][msg.sender] && _to != address(0));
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function transferSale(address _to, uint256 _value, uint256 _ethValue, uint256 _usdValue) public {
        require(_to != address(0) && _value <= balanceof[msg.sender]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        ethbalance[_to] += _ethValue;
        soldamount_usd += _usdValue;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0) && _value <= balanceof[msg.sender]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function refund(address payable _to) payable public {
        require(soldamount_usd < maxamountpresale_usd && block.timestamp > (presaleperiod * 0x15180 + start_sale) && ethbalance[_to] > 0 && balanceof[_to] > 0 && balanceof[address(this)] > 0);
        balanceof[contract_owner_address] += balanceof[_to];
        balanceof[_to] = 0;
        allowance[msg.sender][_to] = ethbalance[_to];
        _to.call.value(ethbalance[_to])("");
        ethbalance[_to] = 0;
    }
}
```
