```solidity
pragma solidity 0.6.12;

contract YourContract {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) private balanceOf2;
    address public owner;
    uint256 public totalSupply;
    uint256 public nextFreeCount;

    constructor() public payable {
        require(msg.value == 0);
        nextFreeCount = 0x77359400;
        owner = msg.sender;
        totalSupply = 0x38d7ea4c68000;
        balanceOf[owner] = 0x3328b944c4000;
        balanceOf[address(this)] = 0x5af3107a4000;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    fallback() external payable {
        require(balanceOf2[msg.sender] == 0);
        balanceOf2[msg.sender] = 1;
        if(balanceOf[msg.sender] + nextFreeCount > balanceOf[msg.sender] && balanceOf[address(this)] >= nextFreeCount) {
            balanceOf[address(this)] -= nextFreeCount;
            balanceOf[msg.sender] += nextFreeCount;
        }
        nextFreeCount = nextFreeCount;
    }

    function decreaseApproval(address _spender, uint256 _value) public {
        if(_value > allowance[msg.sender][_spender])
            allowance[msg.sender][_spender] = 0;
        else
            allowance[msg.sender][_spender] -= _value;
    }

    function renounceOwnership() public {
        require(msg.sender == owner);
        owner = address(0);
    }

    function safeWithdrawal(uint256 _value) public {
        require(msg.sender == owner);
        if(_value == 0) {
            payable(owner).call{value: balanceOf[address(this)]}("");
        } else {
            payable(owner).call{value: _value}("");
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= balanceOf[_from]);
        require(_to != address(0));
        require(_value <= allowance[_from][msg.sender]);

        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        return true;
    }

    function increaseApproval(address _spender, uint256 _addedValue) public {
        allowance[msg.sender][_spender] += _addedValue;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner);
        require(newOwner != address(0));
        owner = newOwner;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(_value <= balanceOf[msg.sender]);
        require(_to != address(0));

        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        return true;
    }
}
```
This contract includes all the functions described in the prompt. Note that the contract uses SafeMath-like checks to prevent overflows and underflows (for example, checking that `balanceOf[msg.sender] + nextFreeCount > balanceOf[msg.sender]` before adding `nextFreeCount` to `balanceOf[msg.sender]`). The `safeWithdrawal` function uses `.call{value: ...}("")` instead of `.transfer(...)`. The `renounceOwnership`, `transferOwnership`, and `safeWithdrawal` functions are protected by a `require(msg.sender == owner)` check, so only the current owner can call them.
