```solidity
pragma solidity 0.6.12;

interface IToken {
    function transfer(address recipient, uint256 amount) external returns (bool);
    function MAX_SUPPLY() external view returns (uint256);
    function totalSupply() external view returns (uint256);
    function updateTotalSupply(uint256 newSupply) external;
}

contract SaleContract {
    IToken public token;
    uint256 public starticophaseone;
    uint256 public starticophasetwo;
    uint256 public starticophasethree;
    uint256 public endico;
    address payable public wallet;
    uint256 public currentrate;
    uint256 public weiraised;
    bool public haltsale;
    address public admin;
    uint256 public teamtokens;
    uint8 public presaledist;

    constructor() public payable {
        currentrate = 1100;
        currentrate = 1000;
        currentrate = 850;
        teamtokens = 0x14adf4b7320334b9000000;
        presaledist = 0;
    }

    function distPresale(address _to, uint256 _value) external {
        require(_value <= 0xac0db698068112d000000 && msg.sender == admin && presaledist == 0, "Conditions not met");
        presaledist = 1;
        token.transfer(_to, _value);
    }

    function finalizeSale() external {
        require(block.timestamp > endico && msg.sender == admin, "Conditions not met");
        uint256 v3 = token.MAX_SUPPLY();
        uint256 v2 = token.totalSupply();
        if (address(this).balance > 0) {
            wallet.transfer(address(this).balance);
            v3 = token.MAX_SUPPLY();
            v2 = token.totalSupply();
            if (v2 < v3) {
                v2 = token.totalSupply();
                v3 = token.MAX_SUPPLY();
                if (v2 <= v3) {
                    uint256 difference = v3 - v2;
                    token.transfer(wallet, difference);
                    token.updateTotalSupply(difference);
                }
            }
        }
    }

    function withdrawTeamTokens() external {
        require(block.timestamp > starticophaseone + 0x1e13380 && msg.sender == admin, "Conditions not met");
        token.transfer(wallet, teamtokens);
    }

    fallback() external payable {
        require(msg.value != 0 && msg.sender != address(0) && !haltsale, "Conditions not met");
        uint256 amount = currentrate * msg.value;
        token.updateTotalSupply(amount);
        token.transfer(msg.sender, amount);
        if (address(this).balance > 0xde0b6b3a7640000) {
            wallet.transfer(address(this).balance);
        }
    }

    function buyTokens(address _to) external payable {
        require(!haltsale && msg.value != 0 && _to != address(0), "Conditions not met");
        uint256 amount = currentrate * msg.value;
        token.updateTotalSupply(amount);
        token.transfer(_to, amount);
        if (address(this).balance > 0xde0b6b3a7640000) {
            wallet.transfer(address(this).balance);
        }
    }
}
```

This contract includes a fallback function to accept Ethers, buyTokens function for buying tokens, distPresale for distributing presale tokens, finalizeSale for finalizing the sale, and withdrawTeamTokens for withdrawing team tokens. 

Note that the conditions for function calls and calculations are strictly checked to prevent reentrancy attacks and to ensure the correct calculations. 

Also note that this contract interacts with a token contract specified by the `token` state variable, make sure to provide a valid ERC20-like token contract address to use this contract properly.
