pragma solidity ^0.6.0;

contract Auction {

    uint256 public _auctionStart;
    uint256 public _biddingTime;
    uint256 public _highestBid;
    address payable public _highestBidder;
    address payable public _beneficiary;
    mapping(address => uint256) public _withdraw;
    bool public ended;

    event HighestBidIncreased(address bidder, uint256 amount);
    event AuctionEnded(address winner, uint256 amount);

    constructor(uint256 biddingTime, address payable beneficiary) public {
        _auctionStart = now;
        _biddingTime = biddingTime;
        _beneficiary = beneficiary;
    }

    function bid() public payable {
        require(
            now <= _auctionStart + _biddingTime,
            "Auction already ended"
        );

        require(
            msg.value > _highestBid,
            "There already is a higher bid"
        );

        if (_highestBidder != address(0)) {
            _withdraw[_highestBidder] += _highestBid;
        }

        _highestBidder = msg.sender;
        _highestBid = msg.value;
        emit HighestBidIncreased(msg.sender, msg.value);
    }

    function withdraw() public returns (bool) {
        uint256 amount = _withdraw[msg.sender];
        if (amount > 0) {
            _withdraw[msg.sender] = 0;

            if (!msg.sender.send(amount)) {
                _withdraw[msg.sender] = amount;
                return false;
            }
        }
        return true;
    }

    function auctionEnd() public {
        require(
            now >= _auctionStart + _biddingTime,
            "Auction not yet ended"
        );
        require(
            !ended,
            "auctionEnd has already been called"
        );

        ended = true;
        emit AuctionEnded(_highestBidder, _highestBid);

        _beneficiary.transfer(_highestBid);
    }
}
```
This is a simplified version of a blind auction contract where bidders can submit their bids during a bidding period. The bids are only revealed at the end of the auction. To prevent a bidder from changing the bid after they see the bids of other participants, the contract binds the bidder to their bid until the end of the auction.

