Here is the Solidity code based on your requirements:

```solidity
pragma solidity 0.6.12;

contract YourContract {
    address public owner;
    mapping(address => uint256) public deposited;
    uint8 public state;
    address payable public wallet;

    constructor(address _owner, address payable _wallet) public {
        owner = _owner;
        wallet = _wallet;
        state = 0;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function renounceOwnership() public onlyOwner {
        owner = address(0);
    }

    function close() public onlyOwner {
        require(state <= 2 && state == 0);
        state = 2;
        (bool success, ) = wallet.call.value(address(this).balance)("");
        require(success, "Transfer failed.");
    }

    function enableRefunds() public onlyOwner {
        require(state <= 2 && state == 0);
        state = 1;
    }

    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0));
        owner = newOwner;
    }

    function refund(address payable _to) public {
        require(state <= 2 && state == 1);
        uint256 amount = deposited[_to];
        require(amount > 0);
        deposited[_to] = 0;
        (bool success, ) = _to.call.value(amount)("");
        require(success, "Refund failed.");
    }

    function deposit(address _from) public payable onlyOwner {
        require(state <= 2 && state == 0);
        uint256 newDeposit = deposited[_from] + msg.value;
        require(newDeposit >= deposited[_from], "Overflow error.");
        deposited[_from] = newDeposit;
    } 
}
```
Please note that the contract will need to be deployed by the owner and the wallet address will need to be provided at deployment. This contract should work as per your requirements. The onlyOwner modifier is used to restrict access to certain functions to the owner only. The contract also checks for potential overflow errors when adding to the deposit.
