```solidity
pragma solidity 0.6.12;

contract MyContract {
    using SafeMath for uint256;

    uint256 public invested;
    mapping (address => uint256) public lastping;
    mapping (address => uint256) public balanceof;
    mapping (address => uint256) public lastrewards;
    uint256 public cumulativeratios;
    mapping (uint256 => address) public participants;
    uint256 public countparticipants;

    constructor() public payable {
        invested = 0;
        cumulativeratios = 0;
        countparticipants = 0;
    }

    function idle() external {
        lastping[msg.sender] = block.timestamp;
    }

    function claimRewards() external {
        invested = invested.add((cumulativeratios.sub(lastrewards[msg.sender]).mul(balanceof[msg.sender])).div(0xd3c21bcecceda1000000));
        balanceof[msg.sender] = balanceof[msg.sender].add((cumulativeratios.sub(lastrewards[msg.sender]).mul(balanceof[msg.sender])).div(0xd3c21bcecceda1000000));
        lastrewards[msg.sender] = cumulativeratios;
    }

    function divest(uint256 amount) external payable {
        require(balanceof[msg.sender] >= amount, "Insufficient balance");
        balanceof[msg.sender] = balanceof[msg.sender].sub(amount);
        invested = invested.sub(amount);
        lastrewards[msg.sender] = cumulativeratios;
        msg.sender.call.value(amount)("");
        if(invested > 0) {
            cumulativeratios = cumulativeratios.add((cumulativeratios.sub(lastrewards[msg.sender]).mul(balanceof[msg.sender])).div(0xd3c21bcecceda1000000).mul(0xd3c21bcecceda1000000).div(invested));
        }
    }

    function invest() external payable {
        lastping[msg.sender] = block.timestamp;
        balanceof[msg.sender] = balanceof[msg.sender].add(msg.value);
        invested = invested.add(msg.value);
        lastrewards[msg.sender] = cumulativeratios;
        if(lastping[msg.sender] == 0) {
            participants[countparticipants] = msg.sender;
            countparticipants = countparticipants.add(1);
        }
        if(invested > 0) {
            cumulativeratios = cumulativeratios.add((cumulativeratios.sub(lastrewards[msg.sender]).mul(balanceof[msg.sender])).div(0xd3c21bcecceda1000000).mul(0xd3c21bcecceda1000000).div(invested));
        }
    }

    function poke(address user) external payable {
        require(balanceof[user] > 0 && block.timestamp >= lastping[user].add(0xc4e0), "Condition not met");
        lastrewards[user] = cumulativeratios;
        lastping[user] = block.timestamp;
        lastrewards[user] = cumulativeratios;
        if(invested > 0) {
            cumulativeratios = cumulativeratios.add((balanceof[user].div(10).mul(0xd3c21bcecceda1000000)).div(invested));
            cumulativeratios = cumulativeratios.add((cumulativeratios.sub(lastrewards[user]).mul(balanceof[user])).div(0xd3c21bcecceda1000000).mul(0xd3c21bcecceda1000000).div(invested));
            cumulativeratios = cumulativeratios.add((cumulativeratios.sub(lastrewards[user]).mul(balanceof[user])).div(0xd3c21bcecceda1000000).mul(0xd3c21bcecceda1000000).div(invested));
        }
    }
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        return a - b;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b > 0, "SafeMath: division by zero");
        uint256 c = a / b;
        return c;
    }
}
