Here is the Solidity contract based on the given specifications:

```solidity
pragma solidity 0.6.12;

contract MyContract {

    address private owner;
    bool public paused;
    address private owner1;
    address public voken;
    address public public_sale;
    uint256 private price;
    uint256 private rate;
    uint256 public txs;
    mapping (address => uint256) private balanceOf;
    bytes31 private s_1_1_32;

    constructor() payable public {
        voken = 0xFfFAb974088Bd5bF3d7E6F522e93Dd7861264cDB;
        public_sale = 0xd4260e4Bfb354259F5e30279cb0D7F784Ea5f37A;
        price = 0xde0b6b3a7640000;
        rate = 0x3baa0c40;
        owner = msg.sender;
        paused = false;
    }

    function acceptOwnership() external {
        require(msg.sender != address(0), "Caller address cannot be 0");
        owner = msg.sender;
        owner1 = address(0);
    }

    function setPaused(bool _paused) external {
        require(msg.sender == owner, "Only owner can set paused");
        paused = _paused;
    }

    function withdrawEther(address payable _to, uint256 _amount) external {
        require(msg.sender == owner, "Only owner can withdraw Ether");
        require(_to != address(0), "To address cannot be 0");
        require(address(this).balance >= _amount, "Not enough balance");
        _to.transfer(_amount);
    }

    function transferOwnership(address _newOwner) external {
        require(msg.sender == owner, "Only owner can transfer ownership");
        require(_newOwner != address(0), "New owner address cannot be 0");
        owner1 = _newOwner;
    }

    function rescueTokens(address _tokenContract, address _to, uint256 _amount) external {
        require(msg.sender == owner, "Only owner can rescue tokens");
        require(_to != address(0), "To address cannot be 0");

        (bool success, bytes memory data) = _tokenContract.call(abi.encodeWithSignature("balanceOf(address)", address(this)));
        require(success, "Call to token contract failed");

        uint256 balance = abi.decode(data, (uint256));
        require(balance >= _amount, "Not enough balance to rescue");

        (success, ) = _tokenContract.call(abi.encodeWithSignature("transfer(address,uint256)", _to, _amount));
        require(success, "Transfer failed");
    }

    fallback() external payable {
        require(!paused, "Contract is paused");
        require(msg.value >= price, "Not enough Ether sent");

        (bool success, bytes memory data) = voken.call(abi.encodeWithSignature("balanceOf(address)", msg.sender));
        require(success, "Call to voken contract failed");

        uint256 balance = abi.decode(data, (uint256));
        require(balance > 0, "No tokens to sell");

        (success, data) = voken.call(abi.encodeWithSignature("whitelisted(address)", msg.sender));
        require(success, "Whitelisting failed");

        uint256 whitelistStatus = abi.decode(data, (uint256));
        require(whitelistStatus == 0, "Not whitelisted");

        (success, data) = public_sale.call(abi.encodeWithSignature("status()"));
        require(success, "Call to public_sale contract failed");

        uint256 saleStatus = abi.decode(data, (uint256));
        require(saleStatus > 0, "Sale is not active");

        txs += 1;
        balanceOf[msg.sender] += 1;

        msg.sender.transfer(msg.value);

        if (!success) {
            (success, ) = voken.call(abi.encodeWithSignature("mint(address,uint256)", msg.sender, rate));
            require(success, "Minting failed");
        }
    }
}
```

Please note that this contract is a simplified version, and lacks the necessary error checking and security measures for a production environment. Also, this contract is assuming that the `voken` and `public_sale` contracts have the necessary methods as described.
