```solidity
pragma solidity 0.6.12;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/math/SafeMath.sol";

contract YourContract {
    using SafeMath for uint256;

    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;

    uint256 private price2;
    uint256 private price1;
    uint256 private price;
    uint8 public currentphase;
    address private owner;

    constructor() public payable {
        owner = msg.sender;
        price2 = 0x3fd2fe16e98b;
        price1 = 0x1deae71abd794;
        price = 0x1372496afb2870;
        currentphase = 0;
    }

    function approve(address _to, uint256 _value) public {
        allowance[msg.sender][_to] = _value;
    }

    function changeTokenPrice(uint256 _price2, uint256 _price1, uint256 _price) public {
        require(msg.sender == owner);
        price2 = _price2;
        price1 = _price1;
        price = _price;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= balanceof[_from]);
        require(_value <= allowance[_from][msg.sender]);
        balanceof[_from] = balanceof[_from].sub(_value);
        balanceof[_to] = balanceof[_to].add(_value);
        allowance[_from][msg.sender] = allowance[_from][msg.sender].sub(_value);
    }

    function finalize() public {
        require(msg.sender == owner);
        msg.sender.transfer(address(this).balance);
    }

    function changePhaseToPreICO2() public {
        require(msg.sender == owner);
        currentphase = 1;
    }

    function changePhaseToICO() public {
        require(msg.sender == owner);
        currentphase = 2;
    }

    function transfer(address _to, uint256 _value) public {
        require(_value <= balanceof[msg.sender]);
        balanceof[msg.sender] = balanceof[msg.sender].sub(_value);
        balanceof[_to] = balanceof[_to].add(_value);
    }

    fallback() external payable {
        require(msg.value > 0);
        if (totalsupply <= 0x422ca8b0a00a425000000 && currentphase == 0 && 0xa56fa5b99019a5c800000 > (totalsupply+((msg.value/price2)*0xde0b6b3a7640000))) {
            balanceof[msg.sender] = balanceof[msg.sender].add((msg.value/price2)*0xde0b6b3a7640000);
            totalsupply = totalsupply.add((msg.value/price2)*0xde0b6b3a7640000);
        } else if (currentphase != 0) {
            if (totalsupply > 0x422ca8b0a00a425000000 && totalsupply <= 0x4f68ca6d8cd91c6000000 && currentphase == 1 && 0xa56fa5b99019a5c800000 > (totalsupply+((msg.value/price1)*0xde0b6b3a7640000))) {
                balanceof[msg.sender] = balanceof[msg.sender].add((msg.value/price1)*0xde0b6b3a7640000);
                totalsupply = totalsupply.add((msg.value/price1)*0xde0b6b3a7640000);
            } else if (totalsupply <= 0xa56fa5b99019a5c800000 && currentphase != 1 && 0xa56fa5b99019a5c800000 > (totalsupply+((msg.value/price)*0xde0b6b3a7640000)) && currentphase == 2 && totalsupply > 0x4f68ca6d8cd91c6000000) {
                balanceof[msg.sender] = balanceof[msg.sender].add((msg.value/price)*0xde0b6b3a7640000);
                totalsupply = totalsupply.add((msg.value/price)*0xde0b6b3a7640000);
            }
        }
    }
}
```

Please note that this contract uses OpenZeppelin's SafeMath library to prevent overflows and underflows. The contract also uses Solidity's `fallback` function to handle Ether transactions that do not match any function in the contract.
