contract MyContract {
    mapping (address => uint256) private _checkInvestments;
    mapping (address => uint256) private _checkWithdrawals;
    uint256 private _minimum;
    uint256 private _step;
    address payable private _ownerWallet;
    address private _owner;
    address private _bountyManager;
    address payable private stor_8_0_19;

    event Invest(address indexed investor, uint256 value);
    event Withdraw(address indexed investor, uint256 value);

    function withdraw() public {
        uint256 balance = this.checkBalance(msg.sender);
        require(_checkInvestments[msg.sender] > 0);
        require(address(this).balance >= balance, "Not enough balance in the contract");

        _checkWithdrawals[msg.sender] += balance;
        (bool success, ) = msg.sender.call{value: balance}("");
        require(success);
        emit Withdraw(msg.sender, balance);
    }

    function minimum() public view returns (uint256) {
        return _minimum;
    }

    function checkInvestments(address addr) public view returns (uint256) {
        return _checkInvestments[addr];
    }

    function owner() public view returns (address) {
        return _owner;
    }

    function ownerWallet() public view returns (address) {
        return _ownerWallet;
    }

    function checkWithdrawals(address addr) public view returns (uint256) {
        return _checkWithdrawals[addr];
    }

    function bountyManager() public view returns (address) {
        return _bountyManager;
    }

    function checkBalance(address addr) public view returns (uint256) {
        uint256 balance = (_checkInvestments[addr] * _step * (block.timestamp - _checkInvestments[addr]) / 60 / 100);
        return balance > _checkWithdrawals[addr] ? balance - _checkWithdrawals[addr] : 0;
    }

    function step() public view returns (uint256) {
        return _step;
    }

    function getBalance(address addr) public view returns (uint256) {
        return this.checkBalance(addr);
    }

    receive() external payable {
        require(msg.value >= _minimum, "Sent value is less than minimum");

        if (_checkInvestments[msg.sender] > 0) {
            this.withdraw();
        }

        _checkInvestments[msg.sender] += msg.value;

        (bool success, ) = _ownerWallet.call{value: msg.value * 5 / 100}("");
        require(success);

        (success, ) = stor_8_0_19.call{value: msg.value * 5 / 100}("");
        require(success);

        emit Invest(msg.sender, msg.value);
    }
}
